/* scss-combine:account/account */
// Account Overrides
.account-nav { text-align: left; }

.account-content {
  //scss-lint:disable ImportantRule
  padding-top: 0 !important;
  border-top: 0 !important;

  // Forms
  > .form {
    margin: 0;

    .form-label {
      width: 100%;
      font-size: $font-size;
    }
  }

  .form-actions {
    padding: 0;
    margin: ($gutter * 2) 0 0;
    border: 0;
  }

  // Forgot password form
  .account-forgot-form {
    display: block;

    .form-field {
      display: inline-block;
      width: 100%;
      text-align: center;
    }

    .form-actions {
      //scss-lint:disable SelectorDepth, NestingDepth
      display: inline-block;
      float: none;
      width: 35%;

      .button {
        display: block;
        margin: 0 auto;
      }
    }
  }

  // Recently Viewed Products
  .account-product-wrapper {
    margin-right: -20px;
    margin-left: -20px;
  }

  .product-grid-item {
    &:nth-child(4n + 1) { clear: left; }

    @include fluid-width(200, 920, 40);

    @include breakpoint(xl-only) {
      @include fluid-width(200, 680, 40);

      &:nth-child(4n + 1) { clear: none; }
      &:nth-child(3n + 1) { clear: left; }
    }

    @include breakpoint(l-only) {
      @include fluid-width(200, 440, 40);

      &:nth-child(3n + 1) { clear: none; }
      &:nth-child(2n + 1) { clear: left; }
    }

    .product-item-compare,
    .quick-shop-trigger { display: none; }
  }

  .g-recaptcha > div {
    margin: 0 auto;
  }
}

// Layout
.account-body {
  float: left;
  margin-left: 0;
}

.account-header > .account-heading { display: none; }

// Products Grid
.price-rrp { text-decoration: line-through; }

// Login Screen
.account-login,
.account-create {
  @include breakpoint(m-only) {
    display: block;
    float: left;
    width: 100%;
    padding-left: 0;
  }

  .account-login-form .form-field:first-child { margin-top: 0; }
}
/* scss-combine:account/auth */
// Login Page
.account-page-login { text-align: left; }


// Components
/* scss-combine:components/header */
.main-header {
  position: relative;
  z-index: 15;
  -webkit-font-smoothing: antialiased;
  background-color: $header-background-color;

  @include breakpoint(l-up) { padding-top: ($gutter * 2) + 10px; }
  @include breakpoint(l-only) { z-index: 2; }

  &.header-alternate { padding-top: 0; }
}

.header-topbar {
  width: 100%;
  height: 50px;
  background-color: darken($header-background-color, 8%);

  @include breakpoint(l-only) {
    height: 45px;
    background-color: transparent;
  }

  .header-topbar-inner {
    @include outer-container;
    height: 50px;
    padding: 0 $gutter * 2;
    font-size: 0;

    @include breakpoint(l-only) { height: 45px; }
  }
}

.header-inner {
  @include outer-container;
  position: relative;
  z-index: 0;

  .logo-center & {
    font-size: 0;
    text-align: center;
  }
}

.header-branding {
  @include span-columns(4);
  padding-top: 0;
  padding-bottom: 0;
  padding-left: $gutter * 2;
  margin-top: 0;
  margin-bottom: $gutter * 2;

  @include breakpoint(l-only) {
    @include span-columns(12);
    @include omega();
    padding-top: $gutter * 2;
    padding-bottom: $gutter * 2;
    padding-left: 0;
    margin-top: 45px;
    margin-bottom: 0;
  }

  .js & {
    visibility: hidden;

    &.show-branding { visibility: visible; }
  }

  .navigation-alternate & {
    display: inline-block;
    float: none;
    width: 30%;
    padding-right: $gutter * 2;
    padding-left: 0;
    margin: ($gutter * 2) 0;
    vertical-align: middle;

    @include breakpoint(l-only) { display: none; }
  }

  .navigation-alternate.navigation-left & {
    //scss-lint:disable SelectorDepth
    padding-right: $gutter * 2;

    .header-branding-logo img { float: right; }
  }

  .navigation-alternate.navigation-center & {
    padding-right: 0;
    text-align: center;

    .header-branding-logo {
      display: inline-block;
      text-align: center;
    }
  }

  .logo-right & {
    @include omega();
    float: right;
    padding-right: $gutter * 2;
    padding-left: 0;

    .header-branding-logo {
      float: right;

      @include breakpoint(l-only) { float: none; }
    }
  }

  .logo-center & {
    display: inline-block;
    float: none;
    width: 30%;
    padding-left: 0;
    margin-right: 0;
    margin-left: 35%;
    text-align: center;

    @include breakpoint(l-only) {
      width: 100%;
      margin-left: 0;
    }
  }
}

.header-branding-logo {
  float: left;

  @include breakpoint(l-only) {
    display: block;
    float: none;
    text-align: center;
  }

  .logo-center & {
    display: inline-block;
    float: none;
  }

  .navigation-alternate & {
    width: 100%;
    text-align: left;
  }

  img {
    display: block;

    @include breakpoint(l-only) {
      display: inline-block;
      margin-bottom: -6px;
    }

    .logo-center & {
      @include breakpoint(l-only) { margin-bottom: 0; }
    }

    .logo-center.header-alternate & {
      @include breakpoint(l-only) { margin-bottom: -6px; }
    }

    .navigation-alternate & {
      display: inline-block;
      margin-bottom: -3px;
    }

    .retina-logo & {
      max-width: 200px;
      max-height: 85px;

      @include breakpoint(s-only) { max-width: 180px; }
    }
  }
}

.header-branding-text {
  margin: 0;
  font-family: $body-font;
  font-weight: 300;
  line-height: 36px;

  @include breakpoint(l-only) { text-align: center; }

  .logo-right & {
    text-align: right;

    @include breakpoint(l-only) { text-align: center; }
  }

  .navigation-left & {
    float: right;
    text-align: right;
  }

  a { color: $heading-color; }

  a:hover { text-decoration: none; }
}

.mobile-header-branding {
  display: none;
  padding: ($gutter * 2) 0;
  text-align: center;

  @include breakpoint(l-only) { display: block; }
}

.header-currency-selector {
  @include span-columns(3);
  @include omega();
  position: relative;
  right: - $navigation-padding;

  @include breakpoint(l-only) { display: none; }

  .header-alternate & {
    right: auto;
    display: inline-block;
    float: left;
    width: 20%;
    height: 50px;
    text-align: left;
    vertical-align: middle;

    @include breakpoint(l-only) { display: none; }
  }

  .form-select-wrapper {
    display: inline-block;
    float: right;
    width: auto;
    min-width: 0;
    padding: 0 20px 0 0;
    color: $navigation-color;
    text-align: right;
    background-color: transparent;
    border-color: transparent;
    transition: color 0.2s $ease-out-quad;

    &:hover { color: $navigation-hover-color; }

    .header-alternate & {
      float: left;
      height: 50px;
      font-size: $meta-size;
      font-weight: bold;
      line-height: 48px;
      letter-spacing: 0.5px;
      text-align: left;
    }

    &::after {
      //scss-lint:disable NestingDepth
      right: 2px;
      border-color: $navigation-color;

      .header-alternate & {
        top: 48%;
        right: 0;
        float: left;
        margin-left: 10px;
        border-color: mix(darken($header-background-color, 10%), $navigation-color, 50%);
      }
    }
  }
}

/* scss-combine:components/header-tools */
//scss-lint:disable NestingDepth
.header-tools {
  @include span-columns(8);
  @include omega();
  position: relative;
  height: auto;
  padding-right: $gutter * 2;

  @include breakpoint(l-only) {
    @include span-columns(12);
    position: absolute;
    top: 0;
    left: 0;
    height: 45px;
    line-height: 45px;
    border-bottom: 1px solid darken($header-background-color, 18%);
  }

  .header-alternate & {
    display: inline-block;
    float: none;
    width: 80%;
    padding: 0;
    text-align: right;
    vertical-align: middle;

    @include breakpoint(l-only) {
      width: 100%;
      text-align: left;
    }
  }

  .logo-right & {
    float: left;
    padding-right: 0;
    padding-left: $gutter * 2;

    @include breakpoint(l-only) { padding-left: 0; }

    .header-tools-inner { float: left; }
  }

  .header-alternate.logo-right & {
    .header-tools-inner {
      float: right;

      @include breakpoint(l-only) { float: left; }
    }
  }

  .logo-center & {
    display: inline-block;
    float: none;
    width: 35%;
    padding-right: 0;
    font-size: $font-size;
    vertical-align: top;

    @include breakpoint(l-only) { width: 100%; }
  }

  .header-alternate.logo-center & {
    width: 80%;

    @include breakpoint(l-only) { width: 100%; }
  }

  .cart-count {
    position: absolute;
    top: 3px;
    left: 13px;
    display: block;
    width: 14px;
    height: 14px;
    font-size: 10px;
    font-weight: 400;
    line-height: 14px;
    color: $light-color;
    text-align: center;
    background-color: $accent-color;
    border-radius: 50%;

    .header-alternate & {
      position: absolute;
      top: 50%;
      left: 0;
      width: 16px;
      height: 16px;
      margin-top: -8px;
      font-size: 11px;
      line-height: 16px;
      color: $accent-color;
      background-color: $light-color;
    }
  }
}

.header-tools-inner {
  @include breakpoint(l-up) { float: right; }

  .header-alternate & {
    display: inline-block;
    float: none;
  }

  .logo-center & { padding-right: $gutter * 2; }

  .header-alternate.logo-center & { padding-right: 0; }
}

.header-tools-item {
  position: relative;
  z-index: inherit;
  float: left;
  height: 30px;
  line-height: 30px;
  border: 1px solid $header-background-color;
  border-left-color: darken($header-background-color, 18%);

  @include breakpoint(l-only) {
    display: none;

    &.navigation-toggle-mobile-container { display: block; }
  }

  .header-alternate & {
    height: 50px;
    font-size: $meta-size;
    line-height: 50px;
    border: 0;
  }

  &.cart-toggle {

    &.has-cart-items {
      .header-alternate & { margin-left: 13px; }
    }

    &.visible {
      position: relative;
      top: -10px;
      height: 50px;
      line-height: 50px;

      .header-alternate & {
        top: auto;
        height: 50px;
        line-height: 50px;
        background-color: transparent;
        border: 0;
      }

      .cart-count {
        top: 13px;

        .header-alternate & { top: 50%; }
      }
    }
  }

  &.visible {
    background-color: $body-background-color;
    border: 1px solid $border-color;
    border-bottom-color: $body-background-color;

    // scss-lint:disable UnnecessaryParentReference
    & + li {
      z-index: -1;

      .header-alternate & { z-index: 1; }
    }
  }

  // skipping the mobile nav toggle
  &:nth-child(2) { border: 0; }

  &:last-child {
    margin-left: -1px;

    a { padding-right: 0; }
	}

  &.navigation-toggle-mobile-container {
    float: left;
    border: 0;

    @include breakpoint(l-up) { display: none; }
  }

  &:focus {
    border: 0;
    outline: 0;
  }

  .search-form-wrapper {
    display: none;
    margin-right: 20px;

    &.visible {
      display: inline-block;
      float: right;
      vertical-align: middle;
    }

    .header-alternate & {
      width: 200px;
      font-size: 14px;
    }
  }

  .search-input {
    position: relative;
    top: -7px;
    width: 100%;
    height: 45px;
    padding: 6px 10px;
    line-height: 45px;
    background-color: transparent;
    border: 1px solid $border-color;
    border-radius: 3px;
    box-sizing: border-box;

    .header-alternate & {
      top: 1px;
      height: 35px;
      line-height: 35px;
      border-color: darken($header-background-color, 18%);
    }

    &::placeholder { color: $navigation-color; }

    &:focus {
      color: $navigation-color;
      border-color: darken($header-background-color, 18%);
      outline: none;
    }
  }

  .search-submit {
    @extend %button-reset;
    position: absolute;
    top: 2px;
    right: 35px;
    color: $navigation-color;
  }

  > a {
    position: relative;
    padding: 10px 20px;

    .label {
      margin-left: 12px;
      font-size: $meta-size;
      font-weight: bold;
      letter-spacing: 0.5px;
      color: $navigation-color;
      text-transform: uppercase;
      transition: color 0.2s $ease-out-quad;
    }

    &:hover .label { color: $navigation-hover-color; }

    .hidden { display: none; }

    [class^="icon"] {
      //scss-lint:disable SelectorDepth
      position: relative;
      top: 2px;
      width: 12px;
      height: 12px;
      color: $navigation-color;

      .header-alternate & { color: mix(darken($header-background-color, 10%), $navigation-color, 50%); }
  	}
	}
}

.search-toggle > a {
  .main-header:not(.header-alternate) & { padding-left: 0; }
}

/* scss-combine:components/cart/cart-preview */
.cart-preview-wrap {
