.footer-description {
  @include span-columns(6);
  display: block;
  float: left;
  padding-right: 15%;

  @include breakpoint(xl-only) {
    @include span-columns(4);
    padding-right: 0;
  }

  @include breakpoint(l-only) {
    width: 100%;
    padding-right: 0;
    text-align: center;

    p {
      padding-right: 10%;
      padding-left: 10%;
      margin-top: 10px;
    }
  }

  p { color: $footer-color; }
}

.footer-currency-selector {
  display: none;
  float: left;
  width: 100%;
  text-align: center;

  @include breakpoint(l-only) { display: block; }

  .footer-currency-selector-title { color: $footer-color; }

  .form-select-wrapper {
    display: inline-block;
    width: auto;
    min-width: 0;
    padding-right: 40px;
    color: $footer-color;
    background-color: $footer-background-color;

    &::after { border-color: $footer-color; }
  }
}

.footer-newsletter {
  width: 100%;

  @include breakpoint(l-only) {
    padding-top: ($gutter * 4);
    padding-bottom: ($gutter * 5);
    margin-top: 30px;
    text-align: center;
    background-color: darken($accent-color, 3%);

    .footer-section-title { color: $light-color; }
  }
}

.footer-newsletter-form {
  @include breakpoint(l-only) {
    padding-right: 10%;
    padding-left: 10%;
  }

  .newsletter-signup-email {
    display: inline-block;
    width: 73%;
    min-width: 0;
    margin-right: 2%;

    @include breakpoint(m-only) { width: 63%;}
  }

  .newsletter-signup-submit {
    //scss-lint:disable NestingDepth
    display: inline-block;
    width: 25%;

    @include breakpoint(m-only) { width: 35%;}

    input {
      height: 43px;

      @if (lightness($footer-background-color) > 50) {
        background-color: $primary-button-background-color;
        border-color: $primary-button-background-color;

        &:hover {
          background-color: $primary-button-hover;
          border-color: $primary-button-hover;
        }
      } @else {
        background-color: mix($footer-background-color, $footer-color, 80%);
        border-color: mix($footer-background-color, $footer-color, 80%);

        &:hover {
          background-color: mix($footer-background-color, $footer-color, 90%);
          border-color: mix($footer-background-color, $footer-color, 90%);
        }
      }
    }
  }
}

.footer-section-title {
  //scss-lint:disable NameFormat
  margin-top: 0;
  font-size: 16px;
  line-height: 16px;
  color: $footer-color;

  @if (stencilFontFamily("heading-font") == "Lora") {
    font-weight: 600;
    text-transform: capitalize;
  }
}

.footer-payment-icons {
  float: left;
  width: 100%;
  margin-top: $gutter * 2;
  text-align: right;

  @include breakpoint(l-only) {
    padding-bottom: 55px;
    margin-top: 0;
    text-align: center;
    background-color: darken($accent-color, 3%);
  }
}

.footer-payment-icons-list {
  float: left;
  width: 100%;

  li {
    display: inline-block;
    margin-right: 10px;
    font-size: 25px;
    color: rgba($footer-color, 0.4);

    &:last-of-type { margin-right: 0; }
  }
}

// Footer Credits
.footer-attribution,
.footer-copyright {
  float: left;
  width: 100%;
  margin-top: 5px;
  color: rgba($footer-color, 0.4);
  text-align: right;

  @include breakpoint(l-only) {
    padding: 0 10%;
    margin-top: 0;
    text-align: center;
    background-color: darken($accent-color, 3%);
  }

  a {
    color: rgba($footer-color, 0.4);
    border-bottom: 1px solid rgba($footer-color, 0.2);

    &:hover { color: $footer-color; }
  }
}

.footer-copyright {
  margin: 0;

  @include breakpoint(l-only) { padding-bottom: $gutter * 2; }

  p { margin: 0; }
}

// Footer contact
.footer-contact {
  letter-spacing: 0.05em;
  color: rgba($footer-color, 0.4);
  text-align: center;
  background-color: darken($footer-background-color, 5%);

  > * {
    display: inline-block;
    margin: $gutter ($gutter / 2);
    vertical-align: middle;

    &:nth-child(2)::before {
      position: relative;
      top: -2px;
      margin-right: $gutter / 1.5;
      font-size: 11px;
      color: rgba($footer-color, 0.2);
      content: "\2022";
    }
  }
}

/* scss-combine:components/banners */
$page-banner-border: darken($banner-background-color, 18%);

.page-banner {
  letter-spacing: 1px;
  color: $light-color;
  background-color: $banner-background-color;
}

.page-banner-inner {
  padding: ($gutter * 2) 0;
  text-align: center;
  border-top: 1px solid $page-banner-border;
  border-right: 0;
  border-left: 0;

  &:first-of-type {
    border-top: 0;

    .page-banner-bottom & { border-top: 1px solid $page-banner-border; }
  }

  &:last-of-type {
    border-bottom: 1px solid $page-banner-border;

    .page-banner-bottom & { border-bottom: 0; }
  }

  > .banner {
    @include outer-container();

    .full-width-banners & {
      max-width: 100%;
      padding: 0 ($gutter * 2);
    }
  }

  .rte img {
    display: inline-block;
    max-width: 100%;
    height: auto;
  }

  p {
    &:first-of-type { margin-top: 0; }
    &:last-of-type { margin-bottom: 0; }
  }
}

.shipping-banner {
  color: $light-color;
  background-color: rgba($success-color, 0.6);

  .page-banner-inner {
    border-top: 1px solid $success-color;

    &:last-of-type { border-bottom: 1px solid $success-color; }
  }
}

/* scss-combine:components/alerts */
// Alert Messages
.alert {
  position: relative;
  letter-spacing: 1px;
  color: $light-color;
  transition: opacity 0.3s ease;

  @include breakpoint(m-only) { text-align: center; }

  &.dismissed { opacity: 0; }

  &.hidden { display: none; }

  .narrow & { text-align: center; }

  .alert-dismiss {
    position: absolute;
    top: 10px;
    right: 10px;
    color: inherit;
    cursor: pointer;
    opacity: 0.8;

    &:hover { opacity: 1; }

    svg {
      display: block;
      width: 13px;
      height: 13px;
      fill: $light-color;
    }
  }

  &.alert-success,
  &.alert-error,
  &.alert-info {
    padding: 15px 20px;
    margin: $gutter 0;
    border: 1px solid $border-color;

    .quick-shop & { margin: 0 0 20px; }
  }

  &.alert-success {
    background-color: rgba($success-color, 0.6);
    border-color: $success-color;
  }

  &.alert-error {
    background-color: rgba($error-color, 0.6);
    border-color: $error-color;
  }

  &.alert-info {
    background-color: rgba($accent-color, 0.6);
    border-color: $accent-color;
  }

  a {
    color: $light-color;
    text-decoration: underline;
  }
}

/* scss-combine:components/faceted-search */
.faceted-search {
  padding-right: 30px;

  @include breakpoint(l-only) {
    display: none;
    padding-right: 0;
    text-align: center;

    &.visible {
      display: block;
    }
  }
}

.faceted-sidebar-toggle {
  display: none;
  margin-bottom: 50px;

  @include breakpoint(l-only) {
    display: inline-block;
  }
}

.faceted-search-title {
  margin-top: 0;
  margin-bottom: 5px;
  font-size: $font-size;
}

.faceted-search-filter {
  margin-bottom: $gutter * 3;
}

.faceted-search-item {

  @include breakpoint(l-only) {
    max-width: 280px;
    margin: 0 auto;
    border-top: 1px solid rgba($border-color, 0.5);

    &:first-of-type {
      border-top: 0;
    }
  }

  a {
    position: relative;
    display: inline-block;
    vertical-align: middle;

    @include breakpoint(l-only) {
      width: 100%;
    }
  }

  .facet-checkbox {
    display: inline-block;
    margin-top: 2px;
    margin-right: 10px;
    vertical-align: middle;

    @include breakpoint(l-only) {
      position: absolute;
      top: 10px;
      left: 10px;
    }
  }

  .faceted-search-item-label {
    position: relative;
    display: inline-block;
    max-width: 100%;
    padding-right: 50px;
    line-height: inherit;
    vertical-align: middle;

    @include breakpoint(l-only) {
      position: static;
      max-width: 240px;
      padding: 5px 0;
    }
  }

  // scss-lint:disable VariableForProperty
  .faceted-search-item-count {
    position: absolute;
    right: 0;
    bottom: 0;
    width: 40px;
    color: rgba($dark-accent-color,0.5);

    @include breakpoint(l-only) {
      position: absolute;
      top: 10px;
      right: 7px;
      width: auto;
      font-size: 14px;
      line-height: 1;
    }
  }
}

// price range
.facet-range-input-group {
  @extend %clearfix;
  position: relative;
  padding-top: 10px;
  padding-right: 80px;

  @include breakpoint(l-only) {
    max-width: 290px;
    margin: 0 auto;
  }
}

.facet-range-inputs {
  .form-field {
    float: left;
    width: 48%;

    &:last-child {
      float: right;
    }

    .form-input {
      width: 100%;
      height: 41px;
      min-height: 0;

      // scss-lint:disable NestingDepth
      &:focus {
        border-color: $accent-color;
        outline: none;
      }
    }
  }
}

.facet-submit {
  position: absolute;
  top: 10px;
  right: 0;
  width: 70px;
  height: 40px;
  padding-right: 0;
  padding-left: 0;
  font-family: $body-font;
  font-size: 13px;
  letter-spacing: 0;
  text-transform: none;
}


// star rating
.facet-rating {
  .star {
    font-size: 12px;
    color: $border-color;

    &.checked {
      color: $accent-color;
    }
  }
}

.facet-selected-filters {
  padding-bottom: 25px;
  margin-bottom: 30px;
  border-bottom: 1px solid $border-color;

  .clear-all {
    display: inline-block;
    width: 100%;
    padding: 2px;
    clear: both;
    font-size: 14px;
    color: $accent-color;
  }
}

.selected-filter-items {
  float: left;
  width: 100%;
  margin-bottom: 18px;
}

.selected-filter-item {
  @extend %ellipsis;
  position: relative;
  display: inline-block;
  float: left;
  padding: 10px 15px;
  margin: 0 7px 7px 0;
  font-size: 13px;
  color: $body-color;
  background-color: darken(mix($body-background-color, $border-color), 2%);
  border-radius: 4px;
  transition: all 0.2s $ease-out-quad;

  .remove {
    position: relative;
    top: 1px;
    padding-left: 7px;
    font-size: 17px;
    line-height: 17px;
    color: $dark-color;
    transition: all 0.2s $ease-out-quad;
  }

  &:hover {
    color: $light-color;
    background-color: $accent-color;

    .remove {
      color: $light-color;
    }
  }
}

/* scss-combine:components/sorting */
// Facet Sorting
.sorting {
  padding-right: 30px;

  @include breakpoint(l-only) { padding-right: 0; }

  .form-label { @extend .show-for-sr; }
}

.sorting-title {
  margin-top: 0;
  margin-bottom: 5px;
  font-size: $font-size;
}

.product-sort-select { margin-bottom: $gutter * 3; }

// Sorting
.product-grid-sorting {
  position: relative;
  margin-bottom: $gutter * 3;

  @include breakpoint(m-only) { margin-bottom: 20px; }

  &:before {
    position: absolute;
    top: 50%;
    display: block;
    width: 100%;
    height: 1px;
    border-bottom: 1px solid $border-color;
    content: "";
  }

  .sorting {
    padding-right: 0;
    margin-top: -$gutter * 2;
    text-align: center;
  }

  .product-sort-select {
    position: relative;
    z-index: 1;
    display: inline-block;
    width: 33.333%;
    padding: $gutter * 2;
    margin-bottom: 0;
    background-color: $body-background-color;

    @include breakpoint(l-only) { width: 50%; }

    @include breakpoint(m-only) {
      width: 100%;
      padding: 0;
    }
  }

  .form-select-wrapper {
    height: 60px;
    line-height: 58px;
    background-color: $body-background-color;
    border-color: $border-color;
  }

  .sorting-title { @extend .show-for-sr; }
}


// Navigation
/* scss-combine:components/navigation/nav-common */
.navigation-wrap {
  float: left;
  width: 100%;
  font-family: $navigation-font;
  background-color: $header-background-color;
}

.navigation {
  @include outer-container;
  padding-right: $gutter * 2;
  padding-left: $gutter * 2;

  @include breakpoint(l-only) {
    padding-right: 20px;
    padding-left: 20px;
  }

  a {
    display: block;
    font-size: $navigation-size;
    line-height: $navigation-size;
    color: $navigation-color;
  }
}

.nav-menu {
  @include span-columns(9, block-collapse);
  padding: 0;
  list-style-type: none;

  @include breakpoint(l-only) {
    //scss-lint:disable ImportantRule - overwriting the Bourbon block-collapse
    width: 100% !important;
  }

  .menu-open & { display: block; }
}

.nav-menu-item {
  position: relative;
  float: left;
  width: 100%;
  border: 1px solid $header-background-color;
  transition: color 0.2s $ease-out-quad;

  > a {
    position: relative;
    padding: $navigation-padding;
    border-bottom: 1px solid transparent;
  }

  > a:hover { color: $navigation-hover-color; }

  > .nav-icon { display: none; }
}

// scss-lint:disable MergeableSelector
.nav-menu-item.has-dropdown {
  position: relative;

  > a {
    padding-right: 40px;

    &::after {
      position: absolute;
      top: 50%;
      right: 0;
      width: 0;
      height: 0;
      margin-top: -2px;
      margin-right: 15px;
      border-top: 5px solid $navigation-color;
      border-right: 5px solid transparent;
      border-left: 5px solid transparent;
      content: "";
      transition: border-color 0.2s $ease-out-quad;
    }

    &:hover::after { border-top-color: $navigation-hover-color; }
  }

  .mobile & > ul { display: none; }
  .mobile &.menu-open > ul { display: block; }
}

// scss-lint:disable MergeableSelector
.nav-menu-item.menu-open {
  z-index: 15;
  border-color: darken($header-background-color, 18%);

  > a {
    position: relative;
    z-index: 2;
    color: $navigation-hover-color;
    background-color: $header-background-color;
    border-bottom: 1px solid $header-background-color;

    &::after { border-top-color: $navigation-hover-color; }
  }
}

.nav-submenu {
  @include opacity(0);
  @include transition(all, 0.1s, ease-in-out);
  position: relative;
  top: 100%;
  display: none;
  padding-top: $navigation-padding;
  padding-bottom: $navigation-padding;
  background-color: $header-background-color;
  visibility: hidden;

  &.visible {
    @include opacity(1);
    display: block;
    visibility: visible;
  }

  a {
    display: block;
    padding: 10px $navigation-padding;

    .has-dropdown & {
      padding-right: $navigation-padding + 20;
    }
  }
}

.nav-submenu-item.has-dropdown {
  > a { position: relative; }

  &.menu-open {
    > a::after {
      border-left-color: $accent-color;
      transform: rotate(90deg);
    }
  }

  &:first-child {
    > a::after { margin-top: -5px; }
  }
}

.navigation-tier-tertiary {
  @include transition(all, 0.1s, ease-in-out);
  position: relative;
  height: 0;
  padding-top: 0;
  padding-bottom: 0;
  margin-top: 0;
  margin-bottom: 0;
  overflow: hidden;
  background-color: darken($header-background-color, 5%);
  transition: all 0.4s $ease-out-quad;

  .menu-open > & {
    height: auto;
    padding-top: 10px;
    padding-bottom: 10px;
    margin-top: 10px;
    margin-bottom: 10px;
  }
}

.nav-icon {
  position: absolute;
  top: 8px;
  right: $navigation-padding;
  left: auto;
  width: rem-calc(20);
  height: rem-calc(20);
  margin-top: rem-calc(0);
  font-size: rem-calc(8);
  text-align: center;
  cursor: pointer;

  @include breakpoint(l-only) {
    right: $gutter + 10;
    color: contrast($header-background-color, 35%);
  }

  &::before {
    display: block;
    margin-top: 7px;
  }

  &:hover { color: $navigation-hover-color; }
}

.js .nav-menu {
  visibility: hidden;

  &.show-menu { visibility: visible; }
}

/* scss-combine:components/navigation/nav-mobile */
@include breakpoint(l-only) {
  .navigation-wrap {
    position: fixed;
    top: 0;
    z-index: 10;
    height: 100vh;
    padding: 0;
    overflow: hidden;
    background-color: rgba($dark-color, 0.7);
    opacity: 0;
    transition: all 0.4s $ease-out-quad;

    @include breakpoint(l-up) {
      background: transparent;
      opacity: 0;
    }

    .mobile-nav-open & { opacity: 1; }

    .mobile-nav-closed & {
      z-index: -2;
      background: transparent;
    }
  }

  .navigation {
    position: relative;
    top: 0;
    left: 0;
    z-index: -1;
    width: 450px;
    height: 100vh;
    padding: 0;
    margin: 0;
    overflow: hidden;
    background-color: $header-background-color;
    transform: translateX(-450px);
    transition: all 0.2s $ease-out-quad;

    > .navigation-toggle-mobile-wrapper {
      width: 100%;
      padding-top: 12px;
      line-height: 20px;
      text-align: right;
    }

    .mobile-nav-open & {
      z-index: 2;
      overflow-y: scroll;
      transform: translateX(0);
    }
  }

  .mobile-actions {
    @include outer-container;
    padding-right: 30px;
    padding-left: 30px;
    border-bottom: 1px solid darken($header-background-color, 18%);

    .mobile-action-item {
      @include span-columns(4, block-collapse);
      padding-top: 20px;
      padding-bottom: 20px;
      text-align: center;
      border-left: 1px solid darken($header-background-color, 18%);

      &:first-child { border: 0; }

      a { line-height: 24px; }
    }

    .cart-count-preview {
      > a,
      .cart-preview { display: inline-block; }

      .cart-count {
        position: relative;
        top: -3px;
        display: inline-block;
        width: 16px;
        height: 16px;
        margin-left: 3px;
        font-size: 11px;
        font-weight: 400;
        line-height: 16px;
        color: $light-color;
        text-align: center;
        background-color: $accent-color;
        border-radius: 50%;
      }
    }
  }

  .nav-menu {
    position: absolute;
    top: 50;
    z-index: 1;
    width: 100%;
    height: 100%;
    max-width: 450px;
    padding: 0;
  }

  .nav-menu-item {
    border: 0;

    &.menu-open {
      margin-bottom: -1px;
      background: contrast($header-background-color, 3%);
      border: 0;

      > a {
        margin-bottom: 20px;
        color: $navigation-color;
        background-color: transparent;
        border: 0;
        border-top: 1px solid transparent;
        border-bottom: 1px solid contrast($header-background-color, 25%);

        // scss-lint:disable NestingDepth, ImportantRule
        &::after { border-top-color: contrast($header-background-color, 35%) !important; }
      }

      > .nav-icon { display: none; }
    }

    &:first-child a { border-top: 0; }

    > a {
      padding-top: 30px;
      padding-right: 20px;
      padding-bottom: 30px;
      padding-left: 20px;
      margin-right: 30px;
      margin-left: 30px;
      border-top: 1px solid darken($header-background-color, 18%);
      transition: none;

      @include breakpoint(m-only) {
        padding-top: 20px;
        padding-bottom: 20px;
      }
    }
  }

  .nav-submenu {
    padding-top: 0;
    padding-bottom: 30px;
    background: transparent;

    @include breakpoint(m-only) { padding-bottom: 20px; }
  }

  .nav-submenu-item .nav-submenu-item { padding-left: 10px; }

  .nav-submenu-item > a {
    padding-top: 5px;
    padding-right: 20px;
    padding-bottom: 5px;
    padding-left: 20px;
    margin-right: 30px;
    margin-left: 30px;
    color: $navigation-color;
  }

  .nav-submenu-item.has-dropdown {
    position: relative;

    > a::after {
      right: auto;
      left: auto;
      margin-top: -5px;
      margin-left: 15px;
      border-left-color: $navigation-color;
    }

    &.menu-open {
      > a::after { border-left-color: $navigation-hover-color; }
    }

    .nav-icon {
      top: 4px;
      right: $navigation-padding + 25;
    }
  }

  .navigation-tier-tertiary {
    padding-top: 0;
    padding-bottom: 0;
    margin-top: 0;
    margin-bottom: 0;
    background-color: $navigation-hover-color;
    transition: none;

    .menu-open > & {
      padding-top: 20px;
      padding-bottom: 20px;
      margin-top: 20px;
      margin-bottom: 20px;
    }
  }

  .navigation-tier-tertiary-item {
    > a {
      padding-right: 20px;
      padding-left: 20px;
      margin-right: 30px;
      margin-left: 30px;
      color: $light-color;
    }
  }

  .mobile-branding {
    width: 100%;
    padding-top: 0;
    padding-right: 50px;
    padding-bottom: 30px;
    padding-left: 50px;
    text-align: center;
    border-bottom: 1px solid darken($header-background-color, 18%);

    > .header-branding-logo {
      max-width: 100%;
      text-align: center;

      img { display: inline-block; }
    }

    > .header-branding-text a {
      font-size: 25px;
      line-height: 25px;
    }
  }

  .search-form-wrapper {
    display: none;
    padding: 10px 40px;
    background-color: darken($header-background-color, 18%);
    border-bottom: 1px darken($header-background-color, 18%);

    &.visible { display: block; }

    .search-input {
      width: 100%;
      padding-top: 10px;
      padding-right: 20px;
      padding-bottom: 10px;
      padding-left: 20px;
      border: 1px solid $border-color;
      border-radius: 4px;
      box-sizing: border-box;

      &:focus {
        border: 1px solid $navigation-hover-color;
        outline: none;
      }
    }
  }

  .navigation-toggle-mobile-wrapper {
    position: relative;
    display: inline-block;
    padding-top: 0;
    padding-right: 20px;
    padding-bottom: 0;
    padding-left: 20px;
    font-size: 25px;
    line-height: 45px;
    vertical-align: top;
    background-color: transparent;
    border: 0;

    &.close { font-size: 20px; }

    > span {
      position: relative;
      top: 1px;
      color: $navigation-color;
    }
  }

  .main-header,
  .main-content,
  .main-footer,
  .page-banner {
    transform: translateX(0);
    transition: all 0.2s $ease-out-quad;
  }

  .mobile-nav-open {
    .main-header,
    .main-content,
    .main-footer,
    .page-banner { transform: translateX(450px); }
  }
}

@include breakpoint(m-only) {
  .navigation {
    width: 300px;
    transform: translateX(-300px);
  }

  .nav-menu { max-width: 300px; }

  .mobile-nav-open {
    .main-header,
    .main-content,
    .main-footer,
    .page-banner { transform: translateX(300px); }
  }
}

/* scss-combine:components/navigation/nav-desktop */
@include breakpoint(l-up) {

  .navigation-wrap {
    position: relative;
    float: none;
    height: auto;
    padding-bottom: ($gutter * 2) - $navigation-padding + 10px;
    overflow: visible;
    border-bottom: 1px solid darken($header-background-color, 18%);

    &.navigation-alternate { padding-bottom: 0; }
  }

  .navigation {
    position: relative;

    .navigation-alternate & { font-size: 0; }
    .navigation-alternate.navigation-left & { padding: 0 0 0 15px; }

    .navigation-alternate.navigation-center & {
      padding: 0;
      text-align: center;
    }
  }

  .mobile-actions,
  .navigation-toggle-mobile-wrapper,
  .mobile-search-wrapper,
  .mobile-branding,
  .navigation .mobile-list-item { display: none; }

  .nav-menu {
    margin-left: -$navigation-padding;

    .navigation-alternate & {
      position: relative;
      right: -$navigation-padding;
      display: inline-block;
      float: none;
      width: 80%;
      margin-left: 0;
      text-align: right;
      vertical-align: middle;
    }

    .navigation-center & {
      right: auto;
      width: 100%;
      text-align: center;
    }

    .navigation-left.navigation-alternate & {
      right: 0;
      text-align: left;
    }

    > li {
      width: auto;
      margin-right: 10px;

      .navigation-alternate & {
        //scss-lint:disable NestingDepth
        display: inline-block;
        float: none;

        &.mobile-list-item { display: none; }
        &:nth-last-child(2) { margin-right: 0; }
      }
    }
  }

  .nav-submenu {
    position: absolute;
    min-width: calc(180px + #{$navigation-size * 3} + #{$navigation-padding});
    margin: -1px 0 0 -1px;
    border: 1px solid darken($header-background-color, 18%);

    .navigation-alternate & { text-align: left; }

    > li > a:hover { color: $navigation-hover-color; }

    //tertiary
    .nav-submenu {
      position: absolute;
      top: 0;

      &.fly-right {
        left: 239px;
      }

      &.fly-left {
        right: 239px;
      }
    }
  }

  .nav-submenu-item {
    position: relative;
  }

  .nav-submenu-item.menu-open {
    > a { color: $navigation-hover-color; }
  }
}


// Products
/* scss-combine:components/products/product-grid */
// Container
.product-grid-container {
  @include outer-container;
  padding-right: $gutter * 2;
  padding-left: $gutter * 2;

  @include breakpoint(l-only) {
