// Thumbnail
.product-grid-item-thumbnail {
  @include maintain-ratio(1 1);
  position: relative;
  max-width: 100%;
  margin: 0 auto;
  overflow: hidden;
  text-align: center;
  background-color: transparent;
  transition: all 0.5s $ease-out-quad;
  backface-visibility: hidden;

  &.aspect-one-by-one     { @include maintain-ratio(1 1); }
  &.aspect-four-by-five   { @include maintain-ratio(4 5); }
  &.aspect-two-by-three   { @include maintain-ratio(2 3); }
  &.aspect-three-by-two   { @include maintain-ratio(3 2); }

  &:hover {
    background-color: $dark-color;

    > a {
      opacity: 0.8;
      transform: scale(1.05);
    }
  }

  > a {
    float: left;
    width: 100%;
    margin: 0 auto;
    overflow: hidden;
    text-align: center;
    background-position: center center;
    background-size: cover;
    opacity: 1;
    transform: scale(1);
    transition: all 0.5s $ease-out-quad;
    backface-visibility: hidden;

    &.cropped-thumbnail-block { @include coverer; }

    .cropped-thumbnail-image {
      @include centerer;
      height: auto;
      max-width: none;
    }

    img { display: none; }

    .no-image { background-color: $light-color; }
  }
}

// Ratings
.product-grid-item-rating-wrap {
  position: absolute;
  top: 0;
  width: 100%;
  text-align: center;

  .product-grid-item-rating {
    position: relative;
    top: -45px;
    opacity: 0;
    transition: all 0.5s $ease-out-quad;

    .product-grid-item-thumbnail:hover & {
      top: $gutter;
      opacity: 1;
    }

    .star { color: $accent-color; }
    .star-empty { color: $light-color; }

    &.no-rating {
      font-size: 12px;
      letter-spacing: 1px;
      color: $light-color;
      text-shadow: 0 0 9px rgba($dark-color, 0.7);
      text-transform: uppercase;
    }
  }
}

// Sale / Sold Out
.product-grid-item-sale-badge {
  position: absolute;
  top: 50%;
  left: 50%;
  width: 140px;
  height: 45px;
  margin: -25px 0 0 -70px;
  line-height: 45px;
  color: $heading-color;
  background-color: $light-color;
  border-radius: 3px;
  opacity: 1;
  visibility: visible;
  transition: all 0.2s $ease-out-quad;
  backface-visibility: hidden;

  .product-grid-item-thumbnail:hover & {
    opacity: 0;
    visibility: hidden;
  }
}

// Compare
.product-item-compare,
.compare-remove-wrap {
  position: absolute;
  bottom: 0;
  width: 100%;
  text-align: center;

  @include breakpoint(m-only) { display: none; }

  > .form-field-checkbox,
  .compare-remove {
    position: relative;
    bottom: -$gutter * 3;
    display: inline-block;
    padding-top: 5px;
    padding-right: 12px;
    padding-bottom: 7px;
    padding-left: 12px;
    font-size: 14px;
    line-height: 14px;
    color: $light-color;
    cursor: pointer;
    opacity: 0;
    transition: all 0.5s $ease-out-quad;

    .form-label-text {
      //scss-lint:disable NestingDepth
      padding-left: 0;
      font-family: $heading-font;
      font-style: normal;
      font-weight: 400;
      letter-spacing: 1px;
      color: $light-color;
      text-transform: uppercase;

      &::before {
        background-color: transparent;
        border: 2px solid $light-color;
      }
    }

    .icon-close {
      padding-left: 5px;
      font-size: 12px;
    }

    .product-grid-item-thumbnail:hover & {
      bottom: $gutter;
      opacity: 1;
    }
  }

  .input-checkbox {
    display: inline-block;
    vertical-align: middle;
  }

  .form-label-text {
    padding-left: 4px;
    vertical-align: middle;
  }
}

// Details
.product-grid-item-details {
  width: 90%;
  margin: 0 auto;
  text-align: center;

  h3 {
    margin-bottom: 5px;
    font-family: $body-font;
    font-size: $font-size * 1.1;
    font-weight: 400;
    text-transform: capitalize;

    a {
      color: $dark-color;
    }
  }

  p {
    margin-top: 5px;
    font-size: 13px;
    font-weight: 400;
  }
}

// Price
.product-grid-item-price {
  font-size: $font-size;
  color: $accent-color;
  text-transform: capitalize;

  // scss-lint:disable VariableForProperty
  .price-rrp {
    padding-right: 10px;
    color: rgba($dark-color, 0.5);
    text-decoration: line-through;
  }

  .price-tax-label {
    display: block;
    font-size: $meta-size;
    font-weight: lighter;
    color: $meta-color;
  }
}

/* scss-combine:components/products/product-images */
// Product Images
.product-slideshow {
  padding-right: 35px;
  transform: translate3d(0, 0, 0);

  @include breakpoint(m-only) {
    padding-right: 0;
    margin-bottom: $gutter * 2;
    text-align: center;
  }
}

.product-slideshow-images {
  position: relative;
  min-height: 20px;

  .spinner {
    @include centerer();
    top: 300px;
    opacity: 1;
    visibility: visible;
    transition: opacity 0.3s $ease-out-quad;

    @include breakpoint(m-only) { display: none; }

    .quick-shop & { display: none; }
  }

  &.flickity-enabled {
    > .product-single-sale-badge { display: block; }

    > .spinner {
      opacity: 0;
      visibility: hidden;
    }
  }

  .product-single-sale-badge {
    position: absolute;
    top: $gutter;
    left: $gutter;
    z-index: 10;
    display: none;
    padding: 10px 20px;
    letter-spacing: 0.1em;
    color: contrast($accent-color, 90%);
    text-transform: uppercase;
    background-color: $accent-color;
  }

  img {
    display: none;
    width: 100%;
    height: auto;
  }

  .flickity-viewport {
    margin-bottom: 0;

    img {
      display: block;
      padding-left: 2px;
    }
  }

  .flickity-page-dots {
    position: absolute;
    bottom: $gutter / 2;
    display: none;

    .visible-controls & { display: block; }
  }

  .flickity-prev-next-button {
    display: none;
    background-color: transparent;

    &:hover .arrow { fill: contrast($accent-color, 30%); }

    .visible-controls & { display: block; }
  }

  .arrow {
    transition: fill 0.3s $ease-out-quad;
    fill: $accent-color;
  }
}

// Product Thumbnails
.product-thumbnails {
  @extend %clearfix;
  display: none;
  padding-left: 2px;
  margin-right: - $gutter / 2;
  margin-left: - $gutter / 2;

  @include breakpoint(m-only) {
    margin-bottom: $gutter / 2;
    font-size: 0;
  }

  .flickity-enabled + & { display: block; }
}

.product-thumbnail {
  @include maintain-ratio(1 1);
  display: inline-block;
  float: left;
  width: calc(25% - #{$gutter});
  padding-bottom: calc(25% - #{$gutter});
  margin-top: $gutter;
  margin-right: $gutter / 2;
  margin-left: $gutter / 2;
  cursor: pointer;
  background-color: transparent;
  background-position: 50% 50%;
  background-size: cover;
  transition: opacity 0.2s $ease-out-quad;

  @include breakpoint(m-only) {
    display: inline-block;
    float: none;
    margin: 10px 5px;
    vertical-align: top;
  }

  &:hover { opacity: 0.75; }
}

