/* scss-combine:components/products/product-options */
// Product options
$product-option-size: 35px;

.product-options,
.product-customizations {
  @include breakpoint(m-only) { text-align: center; }

  .form-field {
    @include breakpoint(m-only) {
      width: 100%;
      padding: 0 20px;
    }
  }

  .form-field-options,
  .form-field-customizations { margin-bottom: 30px; }

  .form-field-title {
    display: block;
    padding-bottom: 8px;
    font-size: $meta-size;
    letter-spacing: 0.1em;
    color: $meta-color;
    text-transform: uppercase;

    @include breakpoint(m-only) { text-align: center; }
  }

  .form-select-wrapper { border-radius: 4px; }
}

// Text Area
.form-field-textarea {
  textarea { width: 100%; }
}

// Radio Fields
.form-field-radio {
  .form-label {
    display: inline-block;

    &:hover {
      cursor: pointer;
      user-select: none;
    }
  }

  .form-input {
    display: inline-block;

    &:hover {
      cursor: pointer;
      user-select: none;
    }
  }

  .form-label-text {
    margin: 0 10px 0 3px;
    font-style: italic;
    color: $meta-color;
    text-transform: capitalize;
  }
