// File Upload Fields
.form-file-wrapper,
.form-selected-file {
  height: 53px;
  padding: 0 7px;
  line-height: 50px;
  border-radius: 4px;

  &::after {
    height: 53px;
    padding: 0 25px;
    font-size: $font-size;
    line-height: 50px;
    border: 0;
    border-radius: 0 4px 4px 0;
