// Inline Fields
.form-field-inline {
  .form-field-title,
  .form-field-control {
    display: inline-block;
    padding-bottom: 0;
    vertical-align: middle;

    @include breakpoint(m-only) {
      display: block;
      text-align: center;
    }
  }

  .form-field-title {
    padding-right: 20px;

    @include breakpoint(m-only) {
      padding-right: 0;
      margin-bottom: 10px;
    }
  }

  .form-input {
    @include breakpoint(m-only) { display: inline-block; }
  }
}

// Checkboxes
.form-field-checkbox {
  .form-checkbox {
    display: inline-block;

    &:hover { cursor: pointer; }
  }

  .form-label-text {
    font-style: italic;
    color: $meta-color;
    text-transform: capitalize;
    user-select: none;

    &:hover { cursor: pointer; }
  }
}

// Dates
.form-field-date {

  .form-field-control {
    @extend %clearfix;
    display: block;
    margin-right: -5px;
    margin-left: -5px;
  }

  .form-date-year,
  .form-date-month,
  .form-date-day {
    display: block;
    float: left;
    width: calc(33.3333% - 10px);
    margin-right: 5px;
    margin-left: 5px;
  }

  .form-select-wrapper { min-width: 0; }
}

// Rectangles
.form-field-rectangle {
  font-size: 0;

  .form-field-control { margin-bottom: - 10px; }

  .form-input { @extend .show-for-sr; }

  .rectangle {
    display: inline-block;
    margin-right: 10px;
    margin-bottom: 10px;
    user-select: none;

    @include breakpoint(m-only) {
      margin-right: 5px;
      margin-left: 5px;
    }
  }

  .rectangle-text {
    display: block;
    height: $product-option-size;
    min-width: $product-option-size;
    padding: 0 10px;
    overflow: hidden;
    font-size: $font-size;
    line-height: $product-option-size - 3;
    color: $input-color;
    text-align: center;
    text-overflow: ellipsis;
    white-space: nowrap;
    cursor: pointer;
    border: 1px solid $input-border-color;
    border-radius: 4px;
    transition: all 0.2s $ease-out-quad;
  }

  .form-input:focus + .rectangle-text { color: $dark-color; }

  .form-input:checked + .rectangle-text {
    color: $accent-color;
    border-color: $accent-color;
  }

  .form-input:hover + .rectangle-text {
    color: $dark-color;
    border-color: $dark-color;
  }
}

// Swatches
.form-field-swatch {
  .form-field-control {
    @extend %clearfix;
    display: inline-block;
    margin: -7px 0;
  }

  .form-input,
  .form-label-text { @extend .show-for-sr; }

  .swatch-wrap {
    position: relative;
    float: left;
    width: $product-option-size;
    height: $product-option-size;
    margin: 7px 15px 7px 0;

    @include breakpoint(m-only) {
      display: inline-block;
      float: none;
      margin: 6px 7px;
    }

    &:last-of-type { margin-right: 0; }

    //scss-lint:disable NestingDepth
    .swatch {
      position: relative;
      display: block;
      width: $product-option-size;
      height: $product-option-size;
      padding: 0;
      margin-right: 10px;
      cursor: pointer;
      transition: all 0.4s $ease-out-quad;
      user-select: none;

      &::before {
        position: absolute;
        top: 0;
        left: 0;
        z-index: 2;
        display: block;
        width: $product-option-size;
        height: $product-option-size;
        content: "";
        box-shadow: inset 0 0 0 1px $border-color;
        transition: all 0.2s $ease-out-quad;
      }

      &::after {
        position: absolute;
        top: 0;
        left: 0;
        z-index: 1;
        display: block;
        width: $product-option-size;
        height: $product-option-size;
        content: "";
        box-shadow: inset 0 0 0 3px $light-color;
        transition: all 0.2s $ease-out-quad;
      }
    }

    .form-input:hover + .swatch {
      &::before { box-shadow: inset 0 0 0 1px $accent-color; }
      &::after { box-shadow: inset 0 0 0 4px $light-color; }
    }

    .form-input:checked + .swatch {
      &::before { box-shadow: inset 0 0 0 1px $dark-color; }
      &::after { box-shadow: inset 0 0 0 4px $light-color; }
    }
  }

  .swatch-color {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-size: cover;
  }

  .swatch-value {
    margin: 7px 0 7px 10px;
    font-style: italic;
    color: $meta-color;
    text-transform: capitalize;

    @include breakpoint(m-only) {
      display: block;
      margin-top: 10px;
    }
  }

  .two-colors {
    .secondary { top: 50%; }
  }

  .three-colors {
    .secondary { top: 33.33%; }
    .tertiary { top: 66.66%; }
  }

  .swatch-pattern-expanded {
    position: absolute;
    bottom: calc(100% + #{$gutter});
    left: 50%;
    z-index: 5;
    display: none;
    padding: 3px;
    background-color: $light-color;
    border: 1px solid $accent-color;
    border-radius: 3px;
    transform: translateX(-50%);

    @include breakpoint(m-only) {
      //scss-lint:disable ImportantRule - Override desktop hover display
      display: none !important;
    }

    img {
      display: block;
      width: 150px;
      max-width: none;
    }
  }

  .swatch-wrap:hover .swatch-pattern-expanded { display: block; }
}

// Product Pick
.form-field-product-list {
  width: 100%;

  .form-field-control {
    background-color: $input-background-color;
    border: 1px solid $input-border-color;
    border-radius: 4px;

    @include breakpoint(m-only) {
      text-align: left;
    }
  }
}

.product-picklist-item,
.product-picklist-no-image {
  position: relative;
  padding: 20px;

  @include breakpoint(m-only) {
    padding-bottom: 20px;
    margin-bottom: 20px;
  }

  &::after {
    position: absolute;
    bottom: 0;
    left: 0;
    display: block;
    width: 100%;
    border-top: 1px solid $input-border-color;
    content: "";
  }

  &:last-child {
    &::after { display: none; }
  }

  .product-picklist-image-wrap {
    padding: 5px;
    border: 1px solid $input-border-color;
    border-radius: 3px;
    transition: all 0.2s $ease-out-quad;
  }

  img {
    display: block;
    opacity: 1;
    transition: all 0.2s $ease-out-quad;
  }

  &:hover {
    cursor: pointer;

    img { opacity: 0.7; }
  }
}

.form-input.product-picklist-radio,
.product-picklist-item-body,
.product-picklist-image-wrap,
.product-picklist-label-text {
  display: inline-block;
  vertical-align: middle;
}

.product-picklist-radio:checked {
  + .product-picklist-item-body {
    color: $accent-color;

    .product-picklist-image-wrap { border-color: $accent-color; }

    .form-label-text {
      color: $accent-color;

      //scss-lint:disable NestingDepth
      &::after { background-color: $accent-color; }
    }
  }
}

.product-picklist-item-body {
  &.picklist-none {
    margin-top: -10px;
    font-style: normal;
  }
}

.product-picklist-label-text {
  margin-left: 10px;
  font-style: italic;
}

.product-picklist-image-wrap {
  max-width: 75px;
  margin-right: 10px;
}

.product-picklist-image { display: block; }

// Product Events
.product-event.form-field {
  margin-bottom: 30px;
  padding-right: 0;
  padding-left: 0;

  .form-field-control {
    @extend %clearfix;
    display: block;
    margin-right: -5px;
    margin-left: -5px;

    @include breakpoint(m-only) {
      margin-right: 0;
      margin-left: 0;
    }
  }

  .form-select-wrapper {
    float: left;
    width: calc(33.3333% - 10px);
    margin-right: 5px;
    margin-bottom: 10px;
    margin-left: 5px;

    @include breakpoint(m-only) {
      float: none;
      width: 50%;
      margin: 0 auto 10px;
    }
  }
}

.product-event-bounds {
  display: block;
  font-size: $meta-size;
  font-weight: lighter;
  color: $meta-color;
}

// Product Quantity
.product-quantity {
  margin-bottom: 30px;

  .form-field-title {
    display: inline-block;
    padding-right: 20px;
    font-size: $meta-size;
    letter-spacing: 0.1em;
    color: $meta-color;
    text-transform: uppercase;
    vertical-align: middle;

    @include breakpoint(m-only) {
      display: block;
      padding-right: 0;
      padding-bottom: 8px;
    }
  }

  .quantity-input-wrap {
    display: inline-block;
    vertical-align: middle;

    @include breakpoint(m-only) { margin-left: -15px; }

    .form-input {
      width: 70px;
      min-width: 0;

      //scss-lint:disable NestingDepth
      &::-webkit-outer-spin-button,
      &::-webkit-inner-spin-button {
        margin: 0;
        -webkit-appearance: none;
      }
    }
  }

  .product-quantity-toggle-wrapper {
    position: absolute;
    top: 5px;
    left: 75px;
  }

  .form-field-control { position: relative; }

  .quantity-toggle {
    display: block;
    width: 16px;
    height: 16px;
    padding: 0;
    margin: 0;
    letter-spacing: 0;
    text-align: center;
  }

  .quantity-decrement { margin-top: 2px; }

  .increment-icon,
  .decrement-icon {
    position: relative;
    top: -1px;
    font-size: 8px;
    line-height: 16px;
  }

  .increment-icon { left: 0.5px; }
}

.option-unavailable {
  opacity: 0.5;

  .form-label-text { text-decoration: line-through; }
}

/* scss-combine:components/products/product-reviews */
// Reviews List
.reviews-header {
  padding-bottom: $gutter;
  margin-bottom: $gutter * 3;
  border-bottom: 1px solid $border-color;
}

.reviews-header-summary {
  display: inline-block;
  width: 100%;
  vertical-align: middle;

  .rating-stars {
    display: inline-block;
    padding-right: 20px;
    line-height: 1;
    vertical-align: middle;

    @include breakpoint(l-only) { display: block; }
  }

  .review-link { margin-left: 4px; }
}

.review-link { margin-top: $gutter; }

// Review Items
.review-item {
  @extend %clearfix;
  float: left;
  width: 100%;
  padding-top: $gutter * 2;
  padding-bottom: $gutter * 2;
  border-top: 1px solid $border-color;

  &:first-of-type {
    padding-top: 0;
    border-top: 0;
  }
}

.review-item-header {
  @include span-columns(9);

  @include breakpoint(l-only) {
    @include span-columns(12);
  }
}

.review-item-stars {
  @include span-columns(3);
  @include omega();

  @include breakpoint(l-only) {
    @include span-columns(12);
  }

  .rating-stars {
    float: right;

    @include breakpoint(l-only) {
      float: none;
      margin-bottom: $gutter;
    }
  }
}

.review-item-body {
  float: left;
  width: 100%;
  margin-bottom: 0;
}

.review-item-title {
  margin-top: 0;
  margin-bottom: 5px;
  font-size: $font-size;
}

// scss-lint:disable VariableForProperty
.review-meta {
  display: inline-block;
  margin-bottom: $gutter;
  color: rgba($meta-color, 0.75);
}

.reviews-rating-summary {
  display: inline-block;
  vertical-align: middle;

  @include breakpoint(l-only) { padding-left: 0; }
}

// Review Modal
.review-modal-container { display: none; }

.review-image-column {
  @include span-columns(6);
  padding-right: 8px;

  @include breakpoint(m-only) { display: none; }
}

// Review Content
.review-image-container {
  position: relative;
  text-align: center;

  .review-image {
    display: block;
    width: 100%;
  }
}

.review-image-caption {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  padding: $gutter;
  padding-bottom: $gutter + 5px;
  text-align: center;
  background-color: rgba($body-background-color, 0.95);
}

.review-form-column {
  @include span-columns(6);
  margin-right: 0;

  @include breakpoint(m-only) {
    @include span-columns(12);
  }

  .g-recaptcha {
    clear: both;
    padding-top: $gutter;

    @include breakpoint(s-only) {
      margin-bottom: -$gutter;
      transform: scale(0.73);
      transform-origin: 0 0;
    }
  }
}

.review-form-heading {
  margin-top: 0;
  margin-bottom: $gutter;

  @include breakpoint(s-only) { font-size: $font-size; }
}

.write-review-form {
  //scss-lint:disable IdSelector, SelectorFormat
  .form-field {
    margin-bottom: $gutter;

    &.review-form-stars {
      padding-bottom: $gutter;
      border-bottom: 1px solid $border-color;

      //scss-lint:disable NestingDepth
      .form-inline-message { display: block; }
    }

    &#revfromname {
      @include span-columns(6);
      float: left;

      @include breakpoint(s-only) {
        @include span-columns(12);
      }
    }

    &#rating-email {
      @include span-columns(6);
      @include omega();
      float: left;

      @include breakpoint(s-only) {
        @include span-columns(12);
      }
    }

    &#rating-title {
      display: block;
      clear: both;
    }

    &.form-field-submit {
      margin-top: 10px;
      text-align: center;
    }

    .review-form-submit { display: inline-block; }

    .form-inline-message {
      font-size: 12px;
      color: $error-color;
    }
  }

  .review-form-captcha {
    text-align: center;

    #recaptcha_widget_div { display: inline-block; }
  }

  .form-label { width: 100%; }

  .form-label-text {
    padding-bottom: 5px;
    color: $accent-color;
  }

  .form-input { width: 100%; }

  .g-recaptcha > div {
    margin: 0 auto;
  }
}

// Rating Stars (clickable)
.input-stars {
  position: relative;
  display: inline-block;

  &.form-field-error { padding-bottom: 20px; }

  .star {
    display: inline-block;
    float: right;
    padding-right: 5px;
    cursor: pointer;

    &:hover .icon-star,
    &:hover ~ .star .icon-star {
      color: $accent-color;
    }
  }

  .icon-star {
    width: 22px;
    height: 22px;
    color: rgba($body-color, 0.4);
  }

  > input:checked {
    ~ label .icon-star {
      color: $accent-color;
    }
  }
}

// Rating Stars (display only)
.rating-stars {
  .star {
    display: inline-block;
    vertical-align: middle;
  }

  &.stars-small   { font-size: 12px; }
  &.stars-medium  { font-size: 17px; }
  &.stars-large   { font-size: 22px; }

  .star-empty { color: rgba($body-color, 0.2); }
  .star-full  { color: $accent-color; }
}

/* scss-combine:components/products/quick-shop */
// Quickshop
.quick-shop-trigger {
  position: absolute;
  top: 50%;
  left: 50%;
  display: none;
  width: 140px;
  height: 45px;
  margin: -25px 0 0 -70px;
  -webkit-font-smoothing: subpixel-antialiased;
  line-height: 45px;
  color: $light-color;
  text-align: center;
  cursor: pointer;
  background-color: transparent;
  border-radius: 3px;
  opacity: 0;
  visibility: hidden;
  transform: translateY(35%);
  transition: all 0.5s $ease-out-quad;
  transition-delay: 0.2s;
  backface-visibility: hidden;

  .quickshop-enabled & {
    display: inline-block;

    @include breakpoint(m-only) { display: none; }
  }

  .product-grid-item-thumbnail:hover & {
    background-color: rgba($accent-color, 0.8);
    opacity: 1;
    visibility: visible;
    transform: translateY(0%);

    &:hover { background-color: $accent-color; }
  }
}

// Overlay
.quick-shop-wrapper {
  @include coverer();
  position: fixed;
  z-index: 15000;
  background-color: rgba($accent-color, 0.85);
  opacity: 0;
  visibility: hidden;
  transition: all 0.2s $ease-out-quad;

  @include breakpoint(m-only) { display: none; }

  &.visible {
    opacity: 1;
    visibility: visible;
  }
}

// Container
.quick-shop {
  //scss-lint:disable NameFormat
  @include centerer();
  z-index: 1000;
  width: 100%;
  max-width: $max-width - ($gutter * 4);
  max-height: calc(100% - #{$gutter * 6});
  padding: $gutter * 2 $gutter * 2 0;
  overflow-y: scroll;
  background-color: $body-background-color;
  opacity: 0;
  transition: opacity 0.4s $ease-out-quad;

  @if (stencilString("container-width") == "full-width") {
    max-width: calc(100% - #{$gutter * 6});
    max-height: calc(100% - #{$gutter * 6});
  }

  @include breakpoint(xl-only) { max-width: calc(100% - #{$gutter * 4}); }
  @include breakpoint(m-only) { display: none; }

  &.visible { opacity: 1; }

  .single-product-slideshow-wrapper {
    @include span-columns(6);
    margin-bottom: $gutter * 2;
  }

  .single-product-slideshow-image { margin-bottom: 0; }
  .product-details-wrapper {
    @include span-columns(6);
    margin-bottom: $gutter * 2;
  }

  .product-thumbnails {
    @extend %clearfix;
    display: block;
    padding-left: 2px;
    margin-right: -8px;
    margin-left: -8px;

    .product-thumbnail {
      //scss-lint:disable NestingDepth, SelectorDepth
      display: block;
      float: left;
      width: calc(25% - 16px);
      margin: 16px 8px 0;

      img { width: 100%;}
    }
  }
}

// Close Button
.quick-shop-close {
  @extend %button-reset;
  position: absolute;
  top: $gutter;
  right: $gutter;
  z-index: 2000;
  font-size: 25px;
  line-height: 1;
  color: $accent-color;

  @media screen and (max-width: $max-width) {
  }
}


// Pages
/* scss-combine:pages/page */
.page-container {
  @include outer-container;
  padding-right: $gutter * 2;
  padding-left: $gutter * 2;

  @include breakpoint(l-only) {
    padding-right: 20px;
    padding-left: 20px;
  }

  .narrow {
    @include span-columns(8);
    @include shift(2);

    @include breakpoint(m-only) {
      @include span-columns(12);
      @include shift(0);
    }
  }
}

.page-header {
  @include outer-container;
  margin-bottom: $gutter * 3;
  text-align: center;
}

.page-heading { margin-top: 0; }

/* scss-combine:pages/product */
// Product Page
.single-product-wrap {
  @include outer-container;
  padding-right: $gutter * 2;
  padding-left: $gutter * 2;

  @include breakpoint(l-only) {
    padding-right: 20px;
    padding-left: 20px;
  }
}

// Product Column Left
.single-product-left {
  @include span-columns(6);

  @include breakpoint(m-only) {
    @include span-columns(12);
  }
}

// Product Column Right
.single-product-right {
  @include span-columns(6);

  @include breakpoint(m-only) {
    @include span-columns(12);
  }
}

/* scss-combine:pages/category */
// Details
.category-details { @include outer-container; }

.category-details-wrap {
  display: table;
  width: 100%;
  max-width: $max-width + ($gutter * 4);
  border-collapse: separate;
  border-spacing: 30px 0;
  table-layout: fixed;

  @include breakpoint(xl-only) {
    padding-right: $gutter * 2;
    padding-left: $gutter * 2;
    margin-right: 0;
    margin-left: 0;
    border-spacing: 0;
  }

  @include breakpoint(l-only) {
    display: block;
    padding-right: 20px;
    padding-left: 20px;
  }
}

.category-details-row {
  display: table-row;

  @include breakpoint(l-only) { display: block; }
}

.category-image,
.category-info {
  display: table-cell;
  width: 50%;
  padding-right: $gutter * 2;
  vertical-align: middle;

  @include breakpoint(xl-only) { padding-right: $gutter * 2.5; }

  @include breakpoint(l-only) {
    display: block;
    width: 100%;
    padding-right: 0;
  }
}

.category-info {
  padding-right: 0;

  @include breakpoint(xl-only) { padding-left: 0; }

  @include breakpoint(l-only) {
    padding-top: $gutter * 3;
    padding-bottom: $gutter;
    padding-left: 0;
  }

  @include breakpoint(m-only) { padding-top: $gutter; }

  .has-image & {
    padding-left: $gutter * 2;

    @include breakpoint(xl-only) { padding-left: $gutter * 3 - 5px; }
    @include breakpoint(l-only) { padding-left: 0; }
  }

  p:last-of-type { margin-bottom: 0; }
}

.category-image {
  @include breakpoint(l-only) { text-align: center; }

  img {
    display: block;

    @include breakpoint(l-only) { display: inline-block; }
  }
}

.category-title {
  //scss-lint:disable NameFormat
  margin-bottom: 0;
  font-size: $page-heading-size;
  text-transform: capitalize;

  @if (stencilFontFamily("heading-font") == "Lora") { font-weight: 600; }

  @include breakpoint(l-only) { font-size: 25px; }
  @include breakpoint(m-only) { font-size: 20px; }

  .has-description & { margin-bottom: 20px; }
}

.category-title,
.category-description {
  width: 66%;
  margin-top: 0;

  @include breakpoint(xl-only) { width: 100%; }

  @include breakpoint(l-only) {
    padding-right: 5%;
    padding-left: 5%;
    text-align: center;
  }

  .has-image & { width: 100%; }

  img { display: none; }
}

.progress-overlay {
  position: absolute;
  top: 0;
  display: none;
  width: 100%;
  height: 100%;
  background-color: rgba($body-background-color, 0.85);

  &.visible { display: block; }

  .inner {
    position: absolute;
    top: 150px;
    left: 50%;
    display: block;
    width: 60px;
    height: 60px;
    margin-top: -30px;
    margin-left: -30px;
    background-color: $body-background-color;
    border: 1px solid $border-color;
    border-radius: 50%;
  }

  .spinner {
    right: auto;
    left: 50%;
    width: 26px;
    height: 26px;
    margin: -13px 0 0 -13px;
  }
}

// Facets
.facet-filter-wrapper {
  display:none;

  &.is-open {
    display:block;
  }
}

.faceted-show-more-button {
  display: block;
  padding-top: 10px;
  font-weight: 700;

  .toggle-link-text-on {
    display:none;
  }
}

.facet-filter-wrapper {
  display:none;

  &.is-open {
    display:block;
  }
}

.faceted-search-option-columns {
  display: none;
}

/* scss-combine:pages/blog */
// Container
.blog-container {
  @include outer-container;
  padding-right: $gutter * 2;
  padding-left: $gutter * 2;
  text-align: center;

  @include breakpoint(l-only) {
    padding-right: 20px;
    padding-left: 20px;
  }
}

// Posts
.blog-list {
  @include span-columns(8);
  @include shift(2);

  @include breakpoint(l-only) {
    width: 100%;
    margin-left: 0;
  }
}

.blog-post {
  @include outer-container;

  @include breakpoint(l-only) {
    padding: 2%;
  }

  &:first-child {
    .blog-post-title {
      margin-top: 0;
    }
  }

  &:last-child {
    .blog-post-content {
      border-bottom-width: 0;
    }
  }
}

// Author

// Post Thumbnail
.blog-post-thumbnail {
  float: left;
  width: 100%;
  margin-top: 8px;
  margin-bottom: 25px;

  > img {
    display: block;
    width: 100%;
    height: auto;
  }
}

// Post Content
.blog-post-content {
  width: 100%;
  padding-bottom: $gutter * 4;
  text-align: left;
  border-bottom: 1px solid $border-color;

  @include breakpoint(m-only) {
    padding-bottom: $gutter * 2;
  }

  &.summary {
    float: left;
    width: 100%;
    text-align: center;

    p {
      @include breakpoint(m-only) {
        margin: 0;
      }
    }
  }

  p {
    float: left;
    width: 100%;
  }

  iframe {
    display: block;
    margin: 0 auto;
  }

  img {
    display: block;
    width: 110%;
    height: auto;
    max-width: 110%;
    margin: 30px 0 0 -5%;

    @include breakpoint(l-only) {
      width: 100%;
      margin-left: 0;
    }
  }

  > *:first-child {
    //scss-lint:disable ImportantRule
    margin-top: 8px !important;

    img {
      margin-top: 0;
    }
  }
}

.blog-post-title {
  width: 100%;
  margin-top: 60px;
  margin-bottom: 10px;
  font-size: 20px;
  text-align: center;

  @include breakpoint(xl-only) {
    margin-top: 20px;
    font-size: 20px;
  }

  @include breakpoint(l-only) {
    @include span-columns(12);
    @include shift(0);
    @include omega();
  }

  .single-post & {
    margin-top: 0;
  }

  a {
    padding: 0 7px 2px;
    color: $heading-color;
    border-bottom: 1px solid transparent;
    transition: border-bottom-color 0.4s $ease-out-quad;

    &:hover { border-bottom-color: $accent-color; }
  }
}

.blog-post-meta {
  margin-bottom: 20px;
  color: $meta-color;
  text-align: center;
}

.blog-post-date,
.blog-post-author {
  display: inline-block;
  vertical-align: middle;
}

.blog-post-author:before {
  position: relative;
  top: -2px;
  padding: 0 5px;
  font-size: 12px;
  color: rgba($meta-color, 0.6);
  content: "\2022";
}

.blog-post-tags {
  float: left;
  width: 100%;
  margin-top: 20px;
  text-align: center;
}

.blog-post-tags-label {
  display: inline-block;
  margin-right: 5px;
  vertical-align: middle;
}

.blog-post-tags-list {
  display: inline-block;
  vertical-align: middle;

  .blog-post-tag {
    display: inline-block;
    margin-right: 10px;

    &:last-child {
      margin-right: 0;
    }
  }
}

.blog-post-sharing {
  @extend %clearfix;
  float: left;
  width: 100%;
  padding-top: $gutter;
  margin-top: $gutter * 2;
  border-top: 1px solid $border-color;

  @include breakpoint(l-only) {
    margin-top: 0;
    text-align: center;
    border-top: 0;

    .sharing-buttons {
      display: inline-block;
    }
  }
}

// Sidebar
.blog-sidebar {
  @include span-columns(3);
  @include omega();
  padding-left: 30px;
  margin-top: 74px;
  margin-bottom: $gutter * 3;
  text-align: left;
  transition: margin-top 0.5s $ease-out-quad;

  // scss-lint:disable ImportantRule
  @include breakpoint(l-only) {
    @include span-columns(12);
    padding-top: $gutter * 3;
    padding-left: 0;
    margin-top: 0 !important;
    margin-right: 0;
    text-align: center;
    border-top: 1px solid $border-color;
  }
}

.blog-sidebar-heading {
  display: block;
  margin-top: 0;
  font-family: $heading-font;
  font-size: 12px;
  letter-spacing: 0.4em;
  color: $dark-color;
  text-transform: uppercase;
}

.blog-sidebar-blog-post-date {
  display: block;
}

/* scss-combine:pages/brands */
.brand-header-logo {
  text-align: center;

  img {
    display: inline-block;
    max-width: 450px;
  }
}

.brands-grid {
  @include outer-container;
  padding-right: $gutter * 2;
  padding-left: $gutter * 2;

  @include breakpoint(l-only) {
    padding-right: 20px;
    padding-left: 20px;
  }
}

.brands-grid-item-list {
  @include row(1260, 92);
}

.brands-grid-item {
  @include fluid-width(200, 435, 35); // 2-col
  float: left;
  margin-bottom: ($gutter * 2);
  background-color: $light-accent-color;

  &:nth-child(2n + 1) { clear: left; }

  // scss-lint:disable DeclarationOrder
  @include breakpoint(m-up) {
    @include fluid-width(200, 740, 70); // 3-col
    margin-bottom: ($gutter * 3);

    &:nth-child(2n + 1) { clear: none; }
    &:nth-child(3n + 1) { clear: left; }

  }

  @include breakpoint(l-up) {
    @include fluid-width(200, 1010, 70); // 4-col
    margin-bottom: ($gutter * 4);

    &:nth-child(3n + 1) { clear: none; }
    &:nth-child(4n + 1) { clear: left; }
  }
}

.brands-grid-item-wrap {
  @include maintain-ratio(1 1);
  position: relative;

  > a {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
  }

  .brands-grid-item-thumbnail,
  .brands-grid-item-text {
    @include centerer();
    width: 80%;
    text-align: center;
  }
}

.brands-grid-item-text {
  .has-image & {
    @extend .show-for-sr;
  }
}

.no-brand-products {
  text-align: center;
}

/* scss-combine:pages/search */
// Search Product Results
.search-header { text-align: center; }

.seach-results-wrapper { @include outer-container; }

.search-results {
  @include span-columns(12);

  &.has-sidebar { @include span-columns(9); }
}

.search-sidebar { @include span-columns(3); }

// Tabs
.search-tabs {
  position: relative;
  z-index: 1;
  display: block;
  width: 100%;
  padding: 0;
  border-bottom: 1px solid $border-color;

  @include breakpoint(m-only) { text-align: center; }
}

.search-tab-title {
  display: inline-block;
  margin-bottom: -1px;
  cursor: pointer;
  border-bottom: 1px solid transparent;

  &.active { border-bottom-color: $accent-color; }

  > a {
    display: block;
    padding: 15px 20px;

    @include breakpoint(s-only) { padding: 6px 12px; }
  }

  > *:first-child { margin-top: 0; }
  > *:last-child { margin-bottom: 0; }
}

.search-tab-content {
  padding-top: 0;

  //scss-lint:disable ImportantRule
  .layout-block {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
  }

  .alert {
    margin-top: 0;
    margin-bottom: 0;
  }
}

.search-tab-content-panel {
  @include outer-container;
  display: none;

  &.active { display: block; }
  .alert { text-align: center; }
}

// Content Results
.search-results-content {
  .search-result-item {
    padding: 50px 0;
    border-top: 1px solid $border-color;

    &:first-of-type {
      padding-top: 0;
      border-top: 0;
    }

    &:last-of-type { padding-bottom: 0; }

    h3 {
      margin-top: 0;
      margin-bottom: 15px;
      line-height: 1;

      @include breakpoint(l-only) { line-height: 1.2; }
    }
  }

  .search-result-item-content { margin-bottom: 0; }
  .search-result-item-more { margin-top: 20px; }
}

/* scss-combine:pages/compare */
// Compare Tab
.compare-tab {
  position: fixed;
  bottom: 0;
  left: 0;
  z-index: 1;
  display: none;
  float: left;
  width: 100%;
  text-align: center;
  background-color: $header-background-color;
  border-top: 1px solid darken($header-background-color, 18%);

  &.animating,
  &.visible {
    display: block;
    transform: translateY(100%);
    transition: transform 0.2s ease;
  }

  &.visible { transform: translateY(0); }
}

// Compare Tab Header
.item-limit { display: none; }

// Compare Tab Item
.compare-items-list { font-size: 0; }

.compare-product-item {
  position: relative;
  display: inline-block;
  width: 23%;
  margin: 1%;
  text-align: center;
}

.compare-product-item-thumbnail {
  display: inline-block;
  width: 20%;
  vertical-align: middle;
}

.compare-product-item-details {
  display: inline-block;
  width: 100%;
  vertical-align: middle;
}

.compare-product-item-title {
  padding-left: $gutter;
  margin-bottom: 0;
  text-transform: capitalize;
}

// Compare Tab Item Remove
.compare-tab-item-remove {
  position: absolute;
  top: 10px;
  right: 10px;
  z-index: 10;
  width: 30px;
  height: 30px;
  font-size: 13px;
  line-height: 31px;
  text-align: center;
  cursor: pointer;
  border-radius: 50%;

  &:hover { color: $accent-color; }
}

// Compare Tab Actions
.compare-tab-actions {
  float: left;
  width: 100%;
  padding: 20px 0;
  background-color: darken($header-background-color, 7%);
  border-top: 1px solid darken($header-background-color, 18%);

  .button-alt { background-color: transparent; }
}

// Compage Page
.compare-grid {
  @include outer-container;
  padding-right: $gutter;
  padding-left: $gutter;

  @include breakpoint(l-only) {
    padding-right: 20px;
    padding-left: 20px;
  }
}

// Compage Page Header
.compare-header {
  margin-bottom: 50px;
  text-align: center;
}

// Compare Page Grid
.compare-grid-item-list {
  @include row(1260, 92);

  &.two-columns {
    padding: 0 20%;

    @include breakpoint(l-only) { padding: 0 10%; }
    @include breakpoint(m-only) { padding: 0; }
  }
}

// Compare Page List Item
.compare-grid-item {
  // scss-lint:disable DeclarationOrder
  @include fluid-width(200, 435, 35); // 2-col
  float: left;
  margin-bottom: $gutter * 4;

  @include breakpoint(l-only) { margin-bottom: $gutter * 3; }
  @include breakpoint(m-only) { margin-bottom: $gutter * 2; }

  &:nth-child(2n + 1) { clear: left; }

  .two-columns & {
    @include fluid-width(200, 435, 35); // 2-col

    &:nth-child(2n + 1) { clear: left; }
    &:nth-child(3n + 1) { clear: none; }
  }

  .three-columns & {
    @include fluid-width(200, 740, 70); // 3-col

    &:nth-child(2n + 1) { clear: none; }
    &:nth-child(3n + 1) { clear: left; }

    @include breakpoint(l-only) {
      @include fluid-width(200, 435, 35); // 2-col

      &:nth-child(2n + 1) { clear: left; }
      &:nth-child(3n + 1) { clear: none; }
    }
  }

  @include breakpoint(m-up) {
    @include fluid-width(200, 740, 70); // 3-col
    margin-bottom: ($gutter * 3);

    &:nth-child(2n + 1) { clear: none; }
    &:nth-child(3n + 1) { clear: left; }

  }

  @include breakpoint(l-up) {
    @include fluid-width(200, 1010, 70); // 4-col
    margin-bottom: ($gutter * 4);

    &:nth-child(3n + 1) { clear: none; }
    &:nth-child(4n + 1) { clear: left; }
  }
}

/* scss-combine:pages/contact */
#form-contact-us {
  .g-recaptcha {
    clear: both;
    padding-top: $gutter * 2;

    @include breakpoint(s-only) {
      transform: scale(0.90);
      transform-origin: 0 0;
    }
  }
}

/* scss-combine:pages/sitemap */
.sitemap-list {
  padding: 0 10%;
  text-align: center;

  @include breakpoint(l-only) {
    padding: 0;
  }
}

.sitemap-item {
  @include span-columns(4);
  @include omega(3n);
  margin-bottom: $gutter * 3;

  @include breakpoint(l-only) {
    @include span-columns(12);
  }
}

/* scss-combine:pages/cart */
// Container
.cart-container {
  @include outer-container;
  padding-right: $gutter * 2;
  padding-left: $gutter * 2;
  text-align: center;

  @include breakpoint(l-only) {
    padding-right: 20px;
    padding-left: 20px;
  }
}

.cart-page-content {
  position: relative;
}

.cart-table {
  width: 100%;
  margin-bottom: 30px;

  @include breakpoint(l-only) {
    margin-bottom: 0;
    border: 0;
  }
}

.cart-table-header {
  @include breakpoint(l-only) {
    display: none;
  }
}

.cart-table-header-item {
  padding: $gutter * 2;
  text-align: right;
  border-top: 1px solid $border-color;
  border-bottom: 1px solid $border-color;

  &.cart-table-header-item-title {
    text-align: left;
  }
}

.cart-item {
  @include breakpoint(l-only) {
    display: block;
    float: left;
    width: 100%;
    padding: 25px 15px;
    margin-bottom: 25px;
    border: 1px solid $border-color;
  }
}

.cart-item-block {
  padding: $gutter * 2;
  text-align: right;
  border: 0;
  border-bottom: 1px solid $border-color;

  @include breakpoint(l-only) {
    display: block;
    padding: 0;
    border: 0;
  }

  .cart-item:nth-last-child(2) & {
    border: 0;
  }
}

.cart-item-image {
  width: 20%;
  text-align: left;

  @include breakpoint(l-only) {
    float: left;
    width: 100%;
    padding-bottom: 20px;
  }
}

.cart-item-image-wrap {
  position: relative;

  @include breakpoint(l-only) {
    max-width: 175px;
    margin: 0 auto;
  }

  @include breakpoint(s-only) {
    max-width: 100px;
    margin: 0 auto;
  }

  > a {
    @include maintain-ratio(1 1);
    display: block;
    background-position: center center;
    background-repeat: no-repeat;
    background-size: cover;

    @include breakpoint(l-only) {
      // scss-lint:disable ImportantRule
      height: auto;
      padding-bottom: 0;
      background-image: none !important;
    }

    img {
      display: none;

      @include breakpoint(l-only) {
        display: block;
      }
    }
  }

  .cart-item-remove {
    position: absolute;
    top: 10px;
    left: 10px;
    display: block;
    width: 25px;
    height: 25px;
    padding-bottom: 0;
    padding-left: 1px;
    font-size: 14px;
    line-height: 25px;
    text-align: center;
    background-color: rgba($light-color, 0.85);
    border-radius: 2px;
    transition: all 0.2s $ease-out-quad;

    &:hover {
      background-color: $light-color;
    }

    &:focus {
      border: 0;
      outline: 0;
    }
  }
}

.cart-item-details {
  width: 46%;
  padding-right: 0;
  padding-left: 0;
  text-align: left;

  @include breakpoint(l-only) {
    float: left;
    width: 100%;
    text-align: center;
  }
}

.cart-item-details-wrap {
  display: table;
  width: 100%;
  padding: 20px 0;

  @include breakpoint(l-only) {
    padding-top: 0;
  }
}

.cart-item-details-wrap-inner {
  display: table-cell;
  vertical-align: middle;

  @include breakpoint(l-only) {
    dl:last-of-type {
      &:after {
        display: inline-block;
        width: 80%;
        margin: 0 auto;
        margin-top: 20px;
        border-top: 1px dashed $border-color;
        content: "";
      }
    }
  }
}

.cart-item-name {
  margin-top: 0;
  margin-bottom: 10px;

  @include breakpoint(l-only) {
    font-size: $font-size;

    &:after {
      display: block;
      width: 80%;
      padding-top: 20px;
      margin: 0 auto;
      border-bottom: 1px dashed $border-color;
      content: "";
    }
  }
}

.cart-item-brand {
  display: inline-block;
  padding: 8px;
  font-size: $font-size;
  line-height: $font-size;
  background-color: $light-accent-color;
  border-radius: 4px;
}

.cart-item-options {
  @extend %clearfix;
  font-size: $meta-size;

  .cart-item-option-title {
    @include span-columns(4);
    margin-right: 0;
    clear: both;
    font-weight: bold;
    text-transform: capitalize;

    @include breakpoint(l-only) {
      @include span-columns(6);
      text-align: right;
    }

    &:hover,
    &:hover + .cart-item-option-description {
      background-color: $light-accent-color;
    }
  }

  .cart-item-option-description {
    @include span-columns(8);
    @include omega();
    margin: 0;
    word-wrap: break-word;

    @include breakpoint(l-only) {
      @include span-columns(6);
      margin: 0;
      text-align: left;
    }
  }
}

.cart-item-info {
  width: 10%;

  @include breakpoint(l-only) {
    float: left;
    width: 100%;
    padding-bottom: 20px;
    text-align: center;

    .cart-item-value {
      &:before {
        content: attr(data-before);
      }
    }
  }
}

.cart-item-discount {
  display: block;
  text-decoration: line-through;

  .cart-preview-list-item-price-quantity { display: none; }
}

.cart-item-quantity {
  width: 14%;

  @include breakpoint(l-only) {
    float: left;
    width: 100%;
    padding-bottom: 20px;
    text-align: center;
  }

  .form-input[type='number'] {
    position: relative;
    right: 25px;
    display: inline-block;
    width: 70px;
  }

  .form-field-increments {
    position: relative;

    @include breakpoint(l-only) {
      .button-group {
        position: relative;
        right: -12px;
        display: inline-block;
      }
    }

    button {
      position: absolute;
      top: 0;
      right: 0;
      width: 20px;
      height: 20px;
      padding: 0;
      margin: 0;
      letter-spacing: 0;
      text-align: center;

      // scss-lint:disable NestingDepth, SelectorDepth
      > .increment-icon {
        position: relative;
        top: -1px;
        font-size: 10px;
      }

      &:first-of-type {
        top: auto;
        bottom: 0;
      }
    }

    // scss-lint:disable QualifyingElement, NestingDepth
    input[type="number"] {
      -moz-appearance: textfield;

      &::-webkit-outer-spin-button,
      &::-webkit-inner-spin-button {
        margin: 0;
        -webkit-appearance: none;
      }
    }
  }
}

.cart-item-total {
  width: 10%;

  @include breakpoint(l-only) {
    float: left;
    width: 100%;
    text-align: center;

    .cart-item-value {
      &:before {
        content: attr(data-before);
      }
    }
  }
}

.cart-item-gift-wrap {
  @include breakpoint(l-only) {
    padding: 0;
    margin: 0;
    border: 0;
  }

  td {
    padding: 0;
    border: 0;
  }

  .form-select-wrapper {
    display: inline-block;
    max-width: 320px;
  }
}

.cart-item-gift-wrap-text {
  display: block;
}

.cart-page-footer {
  @include span-columns(4);
  @include shift(3);
  position: relative;
  float: left;

  @include breakpoint(l-only) {
    @include span-columns(12);
    @include shift(0);
  }
}

.cart-actions {
  @include span-columns(5);
  margin-top: $gutter * 2 + 3px;
  text-align: left;

  @include breakpoint(l-only) {
    @include span-columns(12);
    margin-top: 0;

    > h2 {
      margin-bottom: 30px;
      font-size: $font-size;
      text-align: center;
    }
  }

  .cart-promo-codes,
  .cart-gift-certificate {
    @extend %clearfix;
    margin-bottom: $gutter * 2;

    @include breakpoint(l-only) {
      margin-bottom: 25px;
    }
  }

  .form-input-wrapper {
    @include span-columns(8);

    @include breakpoint(l-only) {
      @include span-columns(8);
    }

    .form-input[type='text'] {
      width: 100%;
      margin: 0;
    }
  }

  [type='submit'] {
    @include span-columns(4);
    @include omega();
  }
}

.cart-extras-tab {
  position: relative;
  display: block;
  width: 100%;
  padding: $gutter;
  border: 1px solid $border-color;

  &:hover {
    cursor: pointer;
  }

  &:after {
    position: absolute;
    top: 50%;
    right: 0;
    width: 0;
    height: 0;
    margin-top: -6px;
    margin-right: 16px;
    border-top: 6px solid transparent;
    border-bottom: 6px solid transparent;
    border-left: 6px solid rgba($navigation-color, 0.5);
    content: "";
    transition: all 0.4s $ease-out-quad;
  }

  &.hidden {
    display: none;
  }
}

.cart-extras {
  padding: $gutter;
  border: 1px solid $border-color;

  form {
    @extend %clearfix;
  }
}

.cart-extras-cancel {
  padding: 0;
  font-family: $body-font;
  font-size: $font-size;
  border: 0;

  &:after {
    margin-right: 16px - $gutter;
    transform: rotate(90deg);
  }
}

.cart-totals {
  position: relative;
  text-align: right;

  @include breakpoint(l-only) {
    margin-top: $gutter * 4;
  }

  .form-input {
    width: 100%;
  }

  .button {
    width: 100%;
    margin-top: $gutter * 2;
  }

  .cart-total-subtotal,
  .cart-total-shipping,
  .cart-total-taxes,
  .cart-total-grandtotal,
  .cart-total-giftwrapping,
  .cart-total-coupons {
    padding: 20px 0;
    border-bottom: 1px solid $border-color;

    &.no-provider {
      padding: 0;
      border: 0;
    }
  }

  .cart-total-grandtotal {
    border-bottom: 0;
  }
}

.cart-totals-heading {
  margin-top: $gutter * 2;
  line-height: 1;

  @include breakpoint(l-only) {
    font-size: $font-size;
    text-align: center;
  }
}


.cart-total-label {
  float: left;
  font-weight: bold;

  &:after {
    content: ":";
  }
}

.shipping-estimator {
  padding: 20px;
  margin-top: 25px;
  margin-bottom: 25px;
  text-align: left;
  background-color: darken($body-background-color, 3%);
  border-radius: 4px;

  .form-label {
    margin-bottom: 5px;
  }

  .form-field {
    margin-bottom: 20px;

    &:last-of-type {
      margin-bottom: 0;
    }

    // scss-lint:disable NestingDepth
    &[class*='alert-'] {
      background-color: transparent;

      .form-label {
        color: $body-color;
      }
    }

    &.alert-success {
      .form-input,
      .form-select {
        border-color: $success-color;
      }
    }

    &.alert-error {
      .form-input,
      .form-select {
        border-color: rgba($error-color,0.8);
      }
    }

    .alert-info {
      display: block;
      margin-top: 5px;
      background: transparent;
    }
  }

  .form-select {
    width: 100%;

    &:focus {
      border-color: $accent-color;
      outline: none;
    }
  }

  .form-input {
    display: inline-block;
    width: 100%;
    opacity: 1;
  }

  .shipping-estimate-submit {
    margin-top: 0;
  }

  // scss-lint:disable VariableForProperty
  .shipping-estimator-error {
    width: 80%;
    padding-top: 15px;
    margin: 0 auto;
    line-height: 1.2;
    color: rgba($error-color, 0.8);
    text-align: center;

    &:after {
      display: block;
      width: 60%;
      margin: 20px auto 0;
      border-bottom: 1px solid $border-color;
      content: "";
    }

    &:last-of-type:after {
      display: none;
    }
  }
}

.shipping-quotes {
  ul:first-of-type {
    margin-top: 30px;
  }
}

.shipping-estimate-show,
.shipping-estimate-hide {
  display: inline-block;
  cursor: pointer;

  &.hidden {
    display: none;
  }
}

.shipping-quotes-method {
  .estimator-form-label,
  .estimator-form-input {
    display: inline-block;
    float: left;
    margin-left: 0;
    vertical-align: middle;

    label {
      width: 100%;
    }
  }

  .estimator-form-label {
    @include span-columns(10);
    text-align: left;

    [type='radio'] {
      position: relative;
      top: -1px;
      margin-right: 5px;
    }

    img {
      display: inline-block;
      vertical-align: middle;
    }
  }

  .estimator-form-input {
    @include span-columns(2);
    text-align: right;
  }
}

// Loading Overlay
.cart-overlay {
  position: absolute;
  top: 0;
  left: 0;
  z-index: 1;
  display: none;
  width: 100%;
  height: 100%;
  background-color: rgba($body-background-color, 0.75);

  .deactivated & {
    display: block;
  }

  .spinner {
    right: auto;
    left: 50%;
    width: 16px;
    margin-left: -8px;
  }
}

// Cart Paypal button
.cart-additional-checkout-buttons {
  @extend %clearfix;
  margin-top: $gutter;
  text-align: right;

  // scss-lint:disable ImportantRule, QualifyingElement
  p {
    float: none !important;
    margin: rem-calc(20 0);
    color: $meta-color;
  }

  form { clear: both;}
}

/* scss-combine:pages/gift-certificates */
.giftcard-nav {
  padding: 0;
  margin-bottom: 50px;
  border-bottom: 1px solid $border-color;

  @include breakpoint(l-only) {
    padding-bottom: 20px;
    margin-bottom: 20px;
  }
}

.giftcard-nav-item {
  display: inline-block;
  padding-bottom: 25px;
  margin-right: $gutter * 2;
  margin-bottom: -1px;
  margin-left: 0;
  line-height: 1;
  color: $body-color;
  border-bottom: 1px solid transparent;

  @include breakpoint(l-only) {
    width: 100%;
    border-bottom: 0;
  }

  &:last-child { margin-right: 0; }

  &.is-active {
    font-weight: bolder;
    color: $accent-color;
    border-bottom-color: $accent-color;
  }
}

.giftcard-heading {
  margin-bottom: 10px;

  + p {
    padding-bottom: 25px;
    margin-top: 0;
    margin-bottom: $gutter;
    font-weight: bolder;
    border-bottom: 1px solid $border-color;
  }
}

.giftcard-form {
  .form-input.form-radio { width: 1px; }
}

.form-giftcard-balance {
  float: left;
  width: 100%;

  .form-field-control {
    float: none;
    width: auto;
  }

  .form-field { margin-top: 0; }

  .form-actions {
    margin: 0;

    .button { margin-bottom: 0; }
  }
}

.giftcard-form-preview {
  iframe {
    padding: 10px;
    margin: 35px 0 15px;
    vertical-align: middle;
    background-color: $body-background-color;
    border: 1px solid $input-border-color;
    border-radius: 3px;
    box-shadow: inset 0 0 0 10px lighten($input-border-color, 12%), inset 0 0 0 11px $input-border-color;
  }
}

/* scss-combine:pages/unavailable */
.maintenance-message { text-align: center; }


// Cart
/* scss-combine:components/cart/item-gift-wrap */

.item-gift-wrap {
  margin-top: 20px;
}

.item-gift-wrap-toggle {
  cursor: pointer;
}

.item-gift-wrap-add {
  display: none;

  &.visible {
    display: block;
  }
}

.gift-wrap-single,
.gift-wrap-multiple,
.gift-wrap-message {
  display: none;

  &.visible {
    display: block;
  }
}

.gift-wrap-image-toggle {
  display: none;
  margin-left: 10px;
  color: $accent-color;
  cursor: pointer;

  @include breakpoint(s-only) {
    margin: 10px 0 0;
  }

  &.visible {
    display: inline-block;
  }

  &:hover {
    color: $meta-color;
  }
}

.gift-wrap-multiple {
  .form-field {
    margin-bottom: 15px;
  }

  &:last-child {
    margin-bottom: 0;
  }
}

.gift-wrap-form {
  padding: 30px;
  text-align: left;
  background: $light-accent-color;
  border-top: 1px solid $border-color;

  @include breakpoint(l-only) {
    margin-top: -25px;
    margin-bottom: 25px;
    border: 1px solid $border-color;
    border-top: 0;
  }

  .form-label {
    width: 100%;
  }

  .form-label-text {
    margin-bottom: 10px;
  }

  .form-select {
    width: 100%;
  }
}

.gift-wrap-choice-title {
  margin: 0;
}

.gift-wrap-choice-description {
  margin-top: 5px;
}

.gift-wrap-options {
  margin-bottom: 15px;

  .form-label-text {
    display: inline-block;
    margin-bottom: 0;
    margin-left: 7px;
  }
}

.gift-wrap-message-text-input {
  width: 100%;
}

.gift-wrap-submit-button {
  margin-top: 15px;
}

.gift-wrap-message {
  margin-top: 15px;
}


// Snippets
/* scss-combine:components/snippets/social-feeds */
// Social Feeds Module
.social-feeds {
  @include outer-container;
}

.social-feeds-wrap {
  display: table;
  width: 100%;
  max-width: $max-width + ($gutter * 4);
  padding-bottom: $gutter * 7;
  border-collapse: separate;
  border-spacing: 35px 0;
  table-layout: fixed;

  @include breakpoint(l-only) {
    border-spacing: 20px;
  }

  @include breakpoint(m-only) {
    display: block;
    padding-bottom: 0;
    border-spacing: 0;
  }
}

.social-feeds-row {
  display: table-row;
  margin-right: 5px;
  margin-left: 5px;

  @include breakpoint(m-only) {
    display: block;
  }
}

.social-feed {
  position: relative;
  display: table-cell;
  width: 50%;
  padding: 30px;
  background-color: $light-accent-color;

  @include breakpoint(m-only) {
    display: block;
    width: 100%;
    margin-bottom: $gutter * 6;
  }

  .twitter-widget img {
    height: $font-size;
    vertical-align: baseline;
  }
}

.social-blog-posts li {
  @include breakpoint(l-only) {
    text-align: center;
  }
}

.social-heading {
  display: block;
  width: 100%;
  margin-top: 0;

  @include breakpoint(l-only) {
    text-align: center;
  }

  [class^="icon"] {
    padding-right: 10px;
  }
}

.social-byline {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: $gutter * 4;
  margin-top: 0;
  margin-bottom: -$gutter * 4;
  line-height: $gutter * 4;
  text-align: center;
}

// Blog Posts
.recent-blog-post p {
  margin-top: 10px;
  margin-bottom: 10px;

  &:last-child {
    margin-bottom: 0;
  }
}

// Twitter
.twitter-widget .tweet-box {

  @include breakpoint(l-only) {
    text-align: center;
  }

  a {
    word-wrap: break-word;
  }
}

/* scss-combine:components/snippets/sharing-buttons */
.sharing-buttons {
  display: block;
  padding-top: 7px;
  font-size: 0;

  .blog-post-sharing & { text-align: center; }
}

.sharing-buttons-label {
  position: relative;
  top: -1px;
  padding-right: 15px;
  font-size: $meta-size;
  letter-spacing: 0.1em;
  color: $meta-color;
  text-transform: uppercase;
}

// scss-lint:disable VariableForProperty
.sharing-button {
  display: inline-block;
  padding-right: 10px;
  font-size: $font-size;
  line-height: $font-size;
  color: rgba($meta-color, 0.8);
  vertical-align: middle;
  transition: all 0.2s $ease-out-quad;

  &.facebook-like {
    margin-left: 5px;

    // fix for FB popup issues
    .fb-like iframe { max-width: none;}
  }

  &:last-of-type { padding-right: 0; }
  &:hover { color: $accent-color; }
}

/* scss-combine:components/snippets/newsletter-signup */
// Newsletter Signup
.newsletter-signup {
  @include outer-container;
}

.newsletter-signup-wrap {
  padding: 40px;
  margin-right: $gutter * 2;
  margin-left: $gutter * 2;
  text-align: center;
  background-color: $light-accent-color;

  @include breakpoint(l-only) {
    margin-right: 20px;
    margin-left: 20px;
  }

  @include breakpoint(m-only) {
    display: none;
  }
}

.newsletter-signup-label {
  float: left;
  padding-right: 45px;
  margin: 0;
  font-size: 20px;
  font-weight: 300;
  line-height: 38px;
  letter-spacing: 0.05em;

  @include breakpoint(l-only) {
    width: 100%;
    padding-right: 0;
    padding-bottom: 20px;
    text-align: center;
  }
}

.newsletter-signup-form {
  display: inline-block;
  vertical-align: middle;

  form {
    float: right;
    width: 360px;

    @include breakpoint(l-only) {
      width: 100%;
    }
  }
}

.newsletter-signup-email {
  @include span-columns(8);
}

.newsletter-signup-submit {
  @include span-columns(4);

  input {
    width: 100%;
    padding-right: 0;
    padding-left: 0;

    @include breakpoint(l-only) {
      background-color: rgba($accent-color, 0.6);
      border-color: rgba($accent-color, 0.6);

      &:hover {
        background-color: $accent-color;
      }
    }
  }
}

