crew/pages/home/shop-callout.scss:
.home-shop-callout {

	.home-shop-callout--container {
		.slider {
			max-width: 900px;
			margin:0 auto;
			*:focus {
				outline:none;
			}
			.item {
				min-height: 195px;
				img {
					padding-top: 35px;
					display: block;
					transition: all 0.3s ease;
					margin:0 auto;
					max-width: 80px;
				}
				&.slick-center {
					img {
						padding-top: 0px;
						max-width: 130px;
					}
					h3 {
						font-size: 20px;
					}
				}
				h3 {
					text-transform: none;
					transition: all 0.3s ease;
					font-weight: bold;
					color:#000;
					font-size: 80%;
				}
			}
		}
		.descriptions {
			span {
				display: none;
				&:first-child {
					display: inline;
				}
				p {
					max-width: 750px;
					color:#000;
					margin:20px auto 40px;
					display: block;
				}
			}
		}
		.slick-arrow {
			position: absolute;
			height: 100%;
			top:50%;
			transform: translateY(-50%);
			cursor: pointer;
			transition: all 0.3s ease;
			font-size: 40px;
			transition:all 0.3s ease;
			width: 50px;
			i {
				position: absolute;
				top:50%;
				transform: translateY(-50%);
				transition: transform 0.3s ease;
			}
			&:hover {
				color:$green;
				&.prev {
					i {
						transform: translate(-5px, -50%);
					}
				}
				&.next {
					i {
						transform: translate(5px, -50%);
					}
				}
			}
			&.prev {
				left:0px;
				z-index: 99;
				background: rgba(246,246,246,1);
				background: -moz-linear-gradient(left, rgba(246,246,246,1) 0%, rgba(255,255,255,0) 100%);
				background: -webkit-gradient(left top, right top, color-stop(0%, rgba(246,246,246,1)), color-stop(100%, rgba(255,255,255,0)));
				background: -webkit-linear-gradient(left, rgba(246,246,246,1) 0%, rgba(255,255,255,0) 100%);
				background: -o-linear-gradient(left, rgba(246,246,246,1) 0%, rgba(255,255,255,0) 100%);
				background: -ms-linear-gradient(left, rgba(246,246,246,1) 0%, rgba(255,255,255,0) 100%);
				background: linear-gradient(to right, rgba(246,246,246,1) 0%, rgba(255,255,255,0) 100%);
				filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#f6f6f6', endColorstr='#ffffff', GradientType=1 );
				i {
					left: 0;
				}
			}
			&.next {
				right: 0px;
				z-index: 99;
				background: rgba(255,255,255,0);
				background: -moz-linear-gradient(left, rgba(255,255,255,0) 0%, rgba(246,246,246,1) 100%);
				background: -webkit-gradient(left top, right top, color-stop(0%, rgba(255,255,255,0)), color-stop(100%, rgba(246,246,246,1)));
				background: -webkit-linear-gradient(left, rgba(255,255,255,0) 0%, rgba(246,246,246,1) 100%);
				background: -o-linear-gradient(left, rgba(255,255,255,0) 0%, rgba(246,246,246,1) 100%);
				background: -ms-linear-gradient(left, rgba(255,255,255,0) 0%, rgba(246,246,246,1) 100%);
				background: linear-gradient(to right, rgba(255,255,255,0) 0%, rgba(246,246,246,1) 100%);
				filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#ffffff', endColorstr='#f6f6f6', GradientType=1 );
				i {
					right: 0;
				}
			}
			&.slick-disabled {
				opacity: 0;
				cursor: default;
			}
		}
	}
}

crew/pages/inner/inner.scss:
.inner-content-wrap {
	.carousel-wrap {
		padding:0;
	}


	.layout-block {
		border-bottom:0;

		&.blue-bg {
			background-color: $turq;
		}
		&.grey-bg {
			background-color: #f6f6f6;
		}

		&.water-stick {
			h1 {
				margin-top: 0;
				text-transform: none;
			}
			.btn {
				margin-top: 30px;
			}
		}
		.centered {
			text-align: center;
		}
		.block-heading {
			text-align: center;
			margin-bottom: 30px;
			h4 {
				font-weight: bold;
				margin:0;
			}
		}

		&.mineralizer-container {
			h1 {
				text-transform: none;
			}
			.mineralizer {
				text-align: center;
				img {
					max-width: 250px;
				}
			}
		}

		&.toxin-filter-container {
			background-color: $grey-light;
			.item {
				text-align: center;
				.title {
					font-weight: bold;
					text-transform: none;
				}
				.desc {
					p {
						padding:0;
						margin:0;
					}
				}
				img {
					max-width: 80px;
					height: auto;
					margin: 0 auto;
					display: block;
					margin-bottom: 20px;
				}
			}
			.top-padding {
				padding-top: 25px;
			}
		}
		&.process {
			*:focus {
				outline:none;
			}
			.slider-container {
				position: relative;
				max-width: 750px;
				margin: 0 auto;
				&:before {
					content:'';
					position: absolute;
					height: 4px;
					width: 100%;
					background-color: #000;
					top:50%;
					left: 0;
					transform:translateY(-50%);
				}
				.slider {
					position: relative;
					&:before, &:after {
						content:'';
						position: absolute;
						width: 4px;
						height: 22px;
						top:50%;
						transform:translateY(-50%);
						background-color: #000;
						z-index: 999;
					}
					&:before {
						left:0;
					}
					&:after {
						right:0;
					}
					.item {
						text-align: center;
						position: relative;
						height: 240px;
						cursor: pointer;
						&:hover {
							.step {
								background-color: $turq + 60;
							}
						}
						.step {
							position: relative;
							width: 82px;
							height: 82px;
							display: inline-block;
							background-color: #fff;
							border:4px solid #000;
							border-radius: 50%;
							position: relative;
							transition: background-color 0.3s ease;
							top: 50%;
							transform:translateY(-50%);
							span {
								position: absolute;
								top: 50%;
								left: 50%;
								transform: translate(-50%,-50%);
								font-weight: normal;
								font-size: 37px;
							}
						}
						h4 {
							margin:0;
							position: absolute;
							top:85%;
							left:50%;
							width: 70%;
							transform:translate(-50%, -20px);
							font-size: 15px;
							text-transform: none;
							font-weight: normal;
							transition: font-weight 0.3s ease;
							span {
								display: block;
								font-weight: normal;
							}
						}
						&.slick-center {
							.step {
								background-color: $turq;
							}
							h4 {
								font-weight: bold;
								span {
									font-weight: normal;
								}
							}
						}
					}
					.slick-arrow {
						position: absolute;
						height: 100%;
						top:50%;
						transform: translateY(-50%);
						cursor: pointer;
						transition: all 0.3s ease;
						font-size: 70px;
						transition:all 0.3s ease;
						width: 50px;
						z-index: 99;
						i {
							position: absolute;
							top:25px;
							transform: translateY(-50%);
							transition: transform 0.3s ease;
							@media screen and (min-width: 1041px) {
								top: 50%;
							}
						}
						&:hover {
							color:$green;
							&.prev {
								i {
									transform: translate(-5px, -50%);
								}
							}
							&.next {
								i {
									transform: translate(5px, -50%);
								}
							}
						}
						&.prev {
							left: 50%;
							transform:translate(-150%, -50%);
							@media screen and (min-width: 1041px) {
								left:-100px;
							}

						}
						&.next {
							right: 50%;
							transform:translate(150%, -50%);
							@media screen and (min-width: 1041px) {
								right: -80px;
							}

						}
						&.slick-disabled {
							opacity: 0;
							cursor: default;
						}
					}
				}
			}
			.associated-slider {
				.desc {
					text-align: center;
					display: none;
					&.active {
						display: block;
					}
					img {
						padding:40px 0 5px;
					}
				}
			}
			.buy-btn {
				padding-top: 40px;
			}
		}

		&.charity {
			padding: 8% 0;
			background: {
				image: url('../img/inner/charity.jpg');
				position: center;
				size:cover;
				repeat:no-repeat;
			}
			text-align: center;
			h4 {
				font-weight: bold;
				margin:0 0 20px;
			}
			p {
				margin-bottom: 40px;
			}
		}

	}

}

crew/pages/inner/water-stick.scss:
.water-stick {
	.video-container {
		margin-top: 40px;
		position: relative;
		padding-bottom: 56.25%; /* 16:9 */
		padding-top: 25px;
		height: 0;
		iframe {
			z-index: 10;
			position: absolute;
			top: 0;
			left: 0;
			width: 100%;
			height: 100%;
		}
		.video-frame {
			background: {
				image: url('../img/inner/video_placeholder.jpg');
				position: center;
				size:cover;
			}
			z-index: 10;
			position: absolute;
			top: 0;
			left: 0;
			width: 100%;
			height: 100%;
			&.playing-video {
				display: none;
			}
		}
		.play-video {
			position: absolute;
			top: 50%;
			left: 50%;
			transform: translate(-50%,-50%);
			width: 65px;
			height: 65px;
			border-radius: 50%;
			background-color:#fff;
			transition: all 0.3s ease;
			background-color: $turq;
			border:2px solid #000;
			cursor: pointer;
			&:hover {
				background-color: rgba($turq, 0.8);
			}
			span {
				position: absolute;
				top: 50%;
				left: 53%;
				transform: translate(-50%,-50%);
