core/structured-data/breadcrumbs:
{{!-- Breadcrumbs: include in base.html --}}

<script id="schema-breadcrumbs">
  var schema = document.createElement('script');
  schema.type = 'application/ld+json';
  schema.text = JSON.stringify({
    "@context": "http://schema.org",
    "@type": "BreadcrumbList",
    "itemListElement": [
      {{#each breadcrumbs}}
        {
          "@type": "ListItem",
          "position": {{@index}} + 1,
          "item": {
            "@id": {{#unless @last}}"{{url}}"{{else}}window.location.href{{/unless}},
            "name": "{{name}}"
          }
        }{{#unless @last}},{{/unless}}
      {{/each}}
    ]
  });

  document.querySelector('body').appendChild(schema);

  var script = document.getElementById('schema-breadcrumbs');
  script.parentElement.removeChild(script);
</script>

components/footer-navigation:
{{!
  @param nav_items
         > accepts "pages", "categories" or "shop_by_brand", passed from parent template
}}

<div class="footer-navigation">

  <ul>
    {{#each nav_items}}
      <li class="footer-navigation-list-item">
        <a href="{{url}}">{{name}}</a>
      </li>
    {{/each}}
    {{#if view_all}}
      {{#if shop_by_brand.length '>' 5}}
        <li class="footer-navigation-list-item">
          <a href="{{urls.brands}}">{{lang 'footer.view_all'}}</a>
        </li>
      {{/if}}
    {{/if}}
    {{#if theme_settings.sitemap}}
      <li class="footer-navigation-list-item">
        <a href="{{urls.sitemap}}">{{lang 'sitemap.heading'}}</a>
      </li>
    {{/if}}
  </ul>
</div>

