pages/account/add-return:
{{#partial "page"}}

  <header class="sub-header">
    {{> components/common/breadcrumbs breadcrumbs}}
  </header>

  <section class="layout-block">

    <div class="page-header">
      <h1 class="page-title">
        {{lang 'core.account.return.heading' id=forms.return.order_id}}
      </h1>
    </div>

    <div class="page-container account-container">
      <div class="page-content page-form account-form">
        {{> core/account/add-return}}
      </div>
    </div>

  </section>

{{/partial}}

{{> layout/base}}

core/account/add-return:
{{#partial 'account-heading'}}
  {{lang 'core.account.return.heading' id=forms.return.order_id}}
{{/partial}}

{{#partial 'account-content'}}
  {{#if forms.return.order_products.length}}
    <form action="{{urls.account.orders.save_new_return}}" class="form" method="post" data-validated-form>
      <input type="hidden" name="order_id" value="{{forms.return.order_id}}">

      <table class="table table-clean account-return-table account-table-tight">
        <tr>
          <th>{{lang 'core.account.return.order_item'}}</th>
          <th>{{lang 'core.account.return.item_price'}}</th>
          <th>{{lang 'core.account.return.quantity'}}</th>
        </tr>

        {{#each forms.return.order_products}}
          <tr>
            <td>{{name}}</td>
            <td>{{price.formatted}}</td>
            <td>{{> core/forms/common/select select_type='range' from=0 to=quantity name=(concat 'return_qty[' (concat id ']'))}}</td>
          </tr>
        {{/each}}
      </table>

      <div class="account-return-fields">
        {{> core/forms/common/select name='return_reason' select_type='array' options=forms.return.reasons required=true display_name=(lang 'core.account.return.reason')}}
        {{> core/forms/common/select name='return_action' select_type='array' options=forms.return.actions required=true display_name=(lang 'core.account.return.action')}}
        {{> core/forms/common/multiline name='return_comments' required=true rows=8 label=(lang 'core.account.return.comments')}}
      </div>

      <div class="form-actions">
        <input class="button account-button-primary" type="submit" value="{{lang 'core.account.return.request_return'}}">
      </div>
    </form>

  {{else}}
    <div class="account-empty">{{lang 'core.account.return.empty'}}</li>

  {{/if}}
{{/partial}}

{{> core/account/_private/layout nav_page='returns' class='add-return'}}

core/forms/common/select:
{{!
  @param display_name string (optional)
         The label text.

  @param required bool (optional)
         If true, this field will be required. Defaults to false.

  @param id string (optional)
         A unique ID for this field.

  @param name string
         The form input name.

  @param private_id string (optional)
         Assigned to the data-field-type attribute. A unique ID provided by the
         Bigcommerce platform. Used to target specific elements, for example
         Country or State inputs.

  @param select_type string (optional)
         Controls how the select options are populated.

         If `range`, options will be generated for numbers using the `from` and
         `to` params.

         If `array`, options will be generated using the `options` param, an
         array of values.

         Otherwise, options will be generated using the `options` param, an
         array of objects with `value` and `label` properties. This is what
         the Bigcommerce platform passes us.

  @param from number
         The starting point for the range. Only used if `select_type` is set to
         `range`.

  @param to number
         The ending point for the range. Only used if `select_type` is set to
         `range`.
}}
<div class="form-field form-field-common form-field-select {{#if required}} form-required{{/if}}" id="{{id}}">
  <label class="form-label">
    <span class="form-field-title">
      {{#if display_name}}
        {{display_name}}
      {{else}}
        {{label}}
      {{/if}}
      {{#if required}}<span class="required-text">{{lang 'common.required'}}</span>{{/if}}
    </span>
    <span class="form-field-control">
      <span class="form-select-wrapper">
        <select
          class="form-input form-select"
          id="select-{{id}}"
          name="{{name}}"
          {{#if required}}required{{/if}}
          aria-required="{{required}}"
          {{#if private_id}}data-field-type="{{private_id}}"{{/if}}>

          {{#if select_type '===' 'range'}}
            {{#for from to}}
              <option value="{{$index}}">{{$index}}</option>
            {{/for}}

          {{else}}
            {{#if select_type '===' 'array'}}
              {{#each options}}
                <option value="{{this}}">{{this}}</option>
              {{/each}}

            {{else}}
              {{#each options}}
                <option value="{{value}}" {{#if selected}}selected{{/if}}>{{label}}</option>
              {{/each}}

            {{/if}}

          {{/if}}
        </select>
      </span>
    </span>
  </label>
</div>

core/forms/common/multiline:
{{!
  Textarea form component

  @param validation_message string
         Custom validation message
}}
<div class="form-field form-field-common form-field-multiline {{#if required}}form-required{{/if}}" id="{{id}}">
  <label class="form-label">
    <span class="form-field-title">
      {{label}}
      {{#if required}}<span class="required-text">{{lang 'common.required'}}</span>{{/if}}
    </span>
    <span class="form-field-control">
      <textarea
        class="form-input"
        id="textarea-{{id}}"
        name="{{name}}"
        placeholder="{{prefill}}"
        rows="{{rows}}"
        {{#if required}}
          required
          {{#if validation_message}}
            data-vd-message-required="{{validation_message}}"
          {{/if}}
        {{/if}}
        aria-required="{{required}}">{{value}}</textarea>
    </span>
  </label>
</div>

core/account/_private/layout:
{{!
  A wrapper for all account pages.

  @block account-heading
         The page's title.

  @block account-content
         The content for the main part of the layout.

  @param class string
         A name for the current page, added as a class to the account wrapper.

  @param nav string
         If "hidden", no nav will be shown.

  @param nav_page string
         If matches a string in navigation.html, that link will be made active.

  @param header string
         If "hidden", no header will be shown.
         If "top", header will be shown above the nav.
         Otherwise, header will be shown above the content.
}}
<section class="account-wrapper account-page-{{class}}{{#if nav '===' 'hidden'}} account-nav-hidden{{/if}}">
  {{#if header '===' 'top'}}{{> core/account/_private/layout-heading}}{{/if}}

  {{#if nav '!==' 'hidden'}}
    {{> core/account/_private/navigation nav_page=nav_page}}
  {{/if}}

  <div class="account-body">
    {{#unless header}}{{> core/account/_private/layout-heading}}{{/unless}}

    <div class="account-content">
      {{#block 'account-content'}}{{/block}}
    </div>
  </div>
</section>

