    {{> components/common/breadcrumbs breadcrumbs}}
  </header>

  <section class="layout-block">

    <div class="page-header">
      <h1 class="page-title">
        {{lang 'core.account.recent_items.heading'}}
      </h1>
    </div>

    <div class="page-container account-container">
      <div class="page-content page-form account-form">
