core/structured-data/breadcrumbs:
{{!-- Breadcrumbs: include in base.html --}}

<script id="schema-breadcrumbs">
  var schema = document.createElement('script');
  schema.type = 'application/ld+json';
  schema.text = JSON.stringify({
    "@context": "http://schema.org",
    "@type": "BreadcrumbList",
    "itemListElement": [
      {{#each breadcrumbs}}
        {
          "@type": "ListItem",
          "position": {{@index}} + 1,
          "item": {
            "@id": {{#unless @last}}"{{url}}"{{else}}window.location.href{{/unless}},
            "name": "{{name}}"
          }
        }{{#unless @last}},{{/unless}}
      {{/each}}
    ]
  });

  document.querySelector('body').appendChild(schema);

  var script = document.getElementById('schema-breadcrumbs');
  script.parentElement.removeChild(script);
</script>

components/footer-navigation:
{{!
  @param nav_items
         > accepts "pages", "categories" or "shop_by_brand", passed from parent template
}}

<div class="footer-navigation">

  <ul>
    {{#each nav_items}}
      <li class="footer-navigation-list-item">
        <a href="{{url}}">{{name}}</a>
      </li>
    {{/each}}
    {{#if view_all}}
      {{#if shop_by_brand.length '>' 5}}
        <li class="footer-navigation-list-item">
          <a href="{{urls.brands}}">{{lang 'footer.view_all'}}</a>
        </li>
      {{/if}}
    {{/if}}
    {{#if theme_settings.sitemap}}
      <li class="footer-navigation-list-item">
        <a href="{{urls.sitemap}}">{{lang 'sitemap.heading'}}</a>
      </li>
    {{/if}}
  </ul>
</div>

components/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
  <div class="currency-selector-wrapper">
    <span class="show-for-sr">{{currency_selector.active_currency_name}}</span>
    <div class="form-select-wrapper">
      <select class="{{#if is-mobile}}mobile-{{/if}}currency-selector" data-model="CurrencySelector" dir="rtl">
        {{#each currency_selector.currencies}}
          <option value="{{{switch_url}}}" {{#if is_active}}selected{{/if}}>
            {{name}}
          </option>
        {{/each}}
      </select>
    </div>
  </div>
{{/if}}

core/forms/common/multiline:
{{!
  Textarea form component

  @param validation_message string
         Custom validation message
}}
<div class="form-field form-field-common form-field-multiline {{#if required}}form-required{{/if}}" id="{{id}}">
  <label class="form-label">
    <span class="form-field-title">
      {{label}}
      {{#if required}}<span class="required-text">{{lang 'common.required'}}</span>{{/if}}
    </span>
    <span class="form-field-control">
      <textarea
        class="form-input"
        id="textarea-{{id}}"
        name="{{name}}"
        placeholder="{{prefill}}"
        rows="{{rows}}"
        {{#if required}}
          required
          {{#if validation_message}}
            data-vd-message-required="{{validation_message}}"
          {{/if}}
        {{/if}}
        aria-required="{{required}}">{{value}}</textarea>
    </span>
  </label>
</div>
